import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

function RegisterComplaint() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const captureMode = new URLSearchParams(search).get('capture') === 'true';

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const fileInputRef = useRef(null);

  const [formData, setFormData] = useState({
    category: 'Garbage',
    description: '',
    address: '',
    lat: '',
    lng: '',
    priority: 'Medium',
  });
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [cameraStream, setCameraStream] = useState(null);
  const [showCamera, setShowCamera] = useState(false);
  const [isGettingLoc, setIsGettingLoc] = useState(false);
  const [locError, setLocError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitOK, setSubmitOK] = useState(false);

  // Reverse geocoding
  const reverseGeocode = useCallback(async (lat, lng) => {
    try {
      const res = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=18`
      );
      const json = await res.json();
      if (json?.display_name) {
        setFormData((p) => ({ ...p, address: json.display_name }));
      }
    } catch (err) {
      console.error('Reverse-geocode error:', err);
    }
  }, []);

  // Get location
  const getLocation = useCallback(() => {
    setIsGettingLoc(true);
    setLocError('');

    if (!navigator.geolocation) {
      setLocError('Geolocation not supported.');
      setIsGettingLoc(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (pos) => {
        const { latitude, longitude } = pos.coords;
        setFormData((p) => ({
          ...p,
          lat: latitude.toFixed(6),
          lng: longitude.toFixed(6),
        }));
        reverseGeocode(latitude, longitude);
        setIsGettingLoc(false);
      },
      (err) => {
        console.error('Geolocation error:', err);
        setLocError('Unable to get location. Please allow location access.');
        setIsGettingLoc(false);
      },
      { enableHighAccuracy: true, timeout: 10000, maximumAge: 300000 }
    );
  }, [reverseGeocode]);

  // Start camera
  const startCamera = useCallback(async () => {
    try {
      // Stop previous stream
      if (cameraStream) {
        cameraStream.getTracks().forEach((t) => t.stop());
      }

      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment', width: { ideal: 1280 }, height: { ideal: 720 } },
        audio: false,
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play();
        };
      }
      setCameraStream(stream);
      setShowCamera(true);
    } catch (err) {
      if (err.name === 'NotAllowedError') {
        alert('Camera permission denied. Please allow camera access and try again.');
      } else {
        alert('Unable to open camera. Please use file upload instead.');
      }
      console.error(err);
    }
  }, [cameraStream]);

  // Stop camera
  const stopCamera = () => {
    if (cameraStream) {
      cameraStream.getTracks().forEach((t) => t.stop());
      setCameraStream(null);
    }
    setShowCamera(false);
  };

  // Take photo - FIXED VERSION using your working test logic
  const takePhoto = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    console.log('Taking photo...');
    console.log('Video element:', video);
    console.log('Canvas element:', canvas);
    
    if (!video || !canvas) {
      alert('Camera elements not ready');
      return;
    }
    
    if (video.videoWidth === 0 || video.videoHeight === 0) {
      alert('Camera not ready. Please wait and try again.');
      return;
    }

    // EXACT same logic as your working test
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);

    const dataUrl = canvas.toDataURL('image/jpeg', 0.8);
    console.log('Generated dataURL:', dataUrl.substring(0, 50) + '...');
    
    // Set preview BEFORE closing camera
    setImagePreview(dataUrl);
    
    // Create file object
    fetch(dataUrl)
      .then((r) => r.blob())
      .then((blob) => {
        const file = new File([blob], `complaint-${Date.now()}.jpg`, { type: 'image/jpeg' });
        setImage(file);
        console.log('File created:', file.name, file.size);
      });

    // Stop camera AFTER setting preview
    stopCamera();
  };

  // Effects
  useEffect(() => {
    getLocation();
    if (captureMode) {
      startCamera();
    }

    return () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach((t) => t.stop());
      }
    };
  }, [captureMode, getLocation, startCamera, cameraStream]);

  // Form handlers
  const onChange = (e) => {
    const { name, value } = e.target;
    setFormData((p) => ({ ...p, [name]: value }));
  };

  const onFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    if (!file.type.startsWith('image/')) {
      alert('Please select an image file');
      return;
    }
    
    if (file.size > 5 * 1024 * 1024) {
      alert('Image size should be less than 5MB');
      return;
    }

    setImage(file);
    const reader = new FileReader();
    reader.onloadend = () => setImagePreview(reader.result);
    reader.readAsDataURL(file);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.address.trim()) {
      alert('Please wait for location to be detected or refresh location');
      return;
    }

    setIsSubmitting(true);

    try {
      const { createComplaint } = await import('../services/complaintService');
      
      const complaintData = {
        category: formData.category,
        description: formData.description,
        priority: formData.priority,
        address: formData.address,
        latitude: Number(formData.lat) || null,
        longitude: Number(formData.lng) || null,
        image: imagePreview, // Base64 string
        status: 'pending',
      };
      
      console.log('Submitting complaint with image:', imagePreview ? 'Yes' : 'No');
      
      const response = await createComplaint(complaintData);
      console.log('Complaint submitted successfully:', response);
      
      setSubmitOK(true);
      
      // Reset form after success
      setTimeout(() => {
        setFormData({
          category: 'Garbage',
          description: '',
          address: '',
          lat: '',
          lng: '',
          priority: 'Medium',
        });
        setImage(null);
        setImagePreview(null);
        setSubmitOK(false);
        if (fileInputRef.current) {
          fileInputRef.current.value = '';
        }
        getLocation(); // Get fresh location
      }, 3000);
      
    } catch (err) {
      alert('Failed to submit complaint. Please try again.');
      console.error('Submit error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      {/* Camera Modal */}
      {showCamera && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-75">
          <div className="bg-white p-6 rounded-lg w-full max-w-md mx-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium">Take a Photo</h3>
              <button 
                onClick={stopCamera}
                className="text-gray-500 hover:text-gray-700"
              >
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            
            <div className="mb-3 p-2 bg-blue-50 border border-blue-200 rounded-md">
              <p className="text-xs text-blue-700">
                📷 Position your camera to capture the issue
              </p>
            </div>

            <video
              ref={videoRef}
              className="w-full rounded-md bg-black"
              style={{ maxHeight: '300px' }}
              playsInline
              muted
              autoPlay
            />
            
            <canvas ref={canvasRef} className="hidden" />

            <div className="mt-4 flex justify-center gap-3">
              <button
                onClick={stopCamera}
                className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={takePhoto}
                className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                📸 Capture Photo
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Main Form */}
      <main className="max-w-4xl mx-auto py-8 px-4">
        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="px-6 py-8 bg-gradient-to-r from-blue-600 to-blue-800">
            <h2 className="text-2xl font-bold text-white">Register a Complaint</h2>
            <p className="mt-2 text-blue-100">Fill out the form below to submit your complaint</p>
          </div>

          <div className="px-6 py-8">
            {submitOK && (
              <div className="mb-6 p-4 rounded-md bg-green-50 border border-green-300 text-green-700">
                ✅ Your complaint has been submitted successfully!
              </div>
            )}

            <form onSubmit={onSubmit} className="space-y-6">
              {/* Category & Priority */}
              <div className="grid sm:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={onChange}
                    className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  >
                    {['Garbage', 'Water Leakage', 'Pothole', 'Electricity Issue', 'Street Light', 'Sewage', 'Other'].map((c) => (
                      <option key={c} value={c}>{c}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Priority</label>
                  <select
                    name="priority"
                    value={formData.priority}
                    onChange={onChange}
                    className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  >
                    {['Low', 'Medium', 'High'].map((p) => (
                      <option key={p} value={p}>{p}</option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Description */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                <textarea
                  name="description"
                  rows={4}
                  required
                  value={formData.description}
                  onChange={onChange}
                  className="w-full border border-gray-300 rounded-md p-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  placeholder="Please provide detailed information about the issue"
                />
              </div>

              {/* Location */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Location</label>
                {isGettingLoc ? (
                  <div className="flex items-center text-blue-600 mb-2">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Getting your location...
                  </div>
                ) : locError ? (
                  <div className="mb-2 p-2 bg-red-50 border border-red-200 rounded-md">
                    <p className="text-sm text-red-600">{locError}</p>
                    <button
                      type="button"
                      onClick={getLocation}
                      className="mt-1 text-sm text-red-600 hover:text-red-800 underline"
                    >
                      Try Again
                    </button>
                  </div>
                ) : formData.lat && formData.lng ? (
                  <div className="mb-2 flex items-center text-green-600">
                    <svg className="mr-2 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>
                    Location detected
                    <button
                      type="button"
                      onClick={getLocation}
                      className="ml-2 text-sm text-blue-600 hover:text-blue-800 underline"
                    >
                      Update
                    </button>
                  </div>
                ) : (
                  <button
                    type="button"
                    onClick={getLocation}
                    className="mb-2 inline-flex items-center px-3 py-2 border border-transparent text-sm font-medium rounded-md text-blue-700 bg-blue-100 hover:bg-blue-200"
                  >
                    <svg className="-ml-1 mr-2 h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>
                    Get Current Location
                  </button>
                )}
                
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={onChange}
                  placeholder="Address will be auto-filled from your location"
                  className="w-full border border-gray-300 rounded-md p-2 bg-gray-50 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  required
                />
                
                {formData.lat && formData.lng && (
                  <p className="mt-1 text-xs text-gray-500">
                    Coordinates: {formData.lat}, {formData.lng}
                  </p>
                )}
              </div>

              {/* Image Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Photo</label>
                <div className="flex items-center gap-3 flex-wrap">
                  <input
                    type="file"
                    accept="image/*"
                    ref={fileInputRef}
                    onChange={onFile}
                    className="hidden"
                    id="fileInput"
                  />
                  <label
                    htmlFor="fileInput"
                    className="px-3 py-2 border border-gray-300 rounded-md cursor-pointer hover:bg-gray-50 text-sm"
                  >
                    Choose File
                  </label>
                  <button
                    type="button"
                    onClick={startCamera}
                    className="px-3 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 text-sm"
                  >
                    📸 Take Photo
                  </button>
                  <span className="text-sm text-gray-600">
                    {image ? (image.name || 'Photo captured') : 'No image selected'}
                  </span>
                </div>
                <p className="mt-1 text-xs text-gray-500">
                  Supported formats: JPG, PNG, GIF. Max size: 5MB
                </p>

                {/* Image Preview */}
                {imagePreview && (
                  <div className="mt-3">
                    <p className="text-sm font-medium text-gray-700 mb-2">Preview:</p>
                    <div className="relative inline-block">
                      <img
                        src={imagePreview}
                        alt="Preview"
                        className="max-h-40 rounded-md border shadow-sm"
                      />
                      <button
                        type="button"
                        onClick={() => {
                          setImage(null);
                          setImagePreview(null);
                          if (fileInputRef.current) {
                            fileInputRef.current.value = '';
                          }
                        }}
                        className="absolute top-1 right-1 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                      >
                        <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                      </button>
                    </div>
                  </div>
                )}
              </div>

              {/* Submit Button */}
              <div className="flex justify-end gap-3">
                <button
                  type="button"
                  onClick={() => navigate('/dashboard')}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="px-6 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 disabled:opacity-60 disabled:cursor-not-allowed"
                >
                  {isSubmitting ? (
                    <>
                      <svg className="animate-spin -ml-1 mr-2 h-4 w-4 inline" fill="none" viewBox="0 0 24 24">
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                      </svg>
                      Submitting...
                    </>
                  ) : (
                    'Submit Complaint'
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      </main>
    </div>
  );
}

export default RegisterComplaint;
