import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import TabSwitcher from '../components/TabSwitcher';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { useMap } from 'react-leaflet';
import api from '../api'; // ← Import your configured API instance

// Fix for default marker icon issue in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Component to handle map view changes
function MapView({ center, zoom }) {
  const map = useMap();

  useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);

  return null;
}

function Complaints() {
  const [activeMainTab, setActiveMainTab] = useState('active');
  const [activeComplaints, setActiveComplaints] = useState([]);
  const [solvedComplaints, setSolvedComplaints] = useState([]);
  const [loading, setLoading] = useState(true); // Add loading state
  const [showMap, setShowMap] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [mapCenter, setMapCenter] = useState([20.5937, 78.9629]);
  const [mapZoom, setMapZoom] = useState(5);

  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        console.log('🔍 Fetching complaints...');
        console.log('📡 API Base URL:', process.env.REACT_APP_API_URL);

        // ✅ Use your api instance instead of complaintService import
        const response = await api.get('/api/complaints');
        console.log('✅ Complaints response:', response.data);

        const complaints = response.data.data || response.data;
        console.log('📊 Total complaints fetched:', complaints.length);

        // Filter complaints by status
        const active = complaints.filter(c => 
          c.status !== 'resolved' && c.status !== 'Resolved'
        );
        const solved = complaints.filter(c => 
          c.status === 'resolved' || c.status === 'Resolved'
        );

        console.log('🔄 Active complaints:', active.length);
        console.log('✅ Solved complaints:', solved.length);

        setActiveComplaints(active);
        setSolvedComplaints(solved);

      } catch (error) {
        console.error('❌ Error fetching complaints:', error);
        console.error('🔍 Error details:', error.response?.data);

        // Fallback to localStorage if API fails
        const storedComplaints = JSON.parse(localStorage.getItem('complaints') || '[]');
        console.log('💾 Fallback to localStorage:', storedComplaints.length, 'complaints');

        const active = storedComplaints.filter(c => c.status !== 'Resolved');
        const solved = storedComplaints.filter(c => c.status === 'Resolved');

        setActiveComplaints(active);
        setSolvedComplaints(solved);
      } finally {
        setLoading(false);
      }
    };

    fetchComplaints();
  }, []);

  // Filter active complaints by status
  const pendingComplaints = activeComplaints.filter(c => 
    c.status === 'pending' || c.status === 'Pending'
  );
  const assignedComplaints = activeComplaints.filter(c => 
    c.status === 'assigned' || c.status === 'Assigned'
  );
  const inProgressComplaints = activeComplaints.filter(c => 
    c.status === 'in progress' || c.status === 'In Progress'
  );

  // Filter solved complaints by resolution time
  const thisWeekComplaints = solvedComplaints.filter(c => {
    if (!c.resolvedAt && !c.updatedAt) return false;
    const resolvedDate = new Date(c.resolvedAt || c.updatedAt);
    const today = new Date();
    const oneWeekAgo = new Date(today.getTime() - 7 * 24 * 60 * 60 * 1000);
    return resolvedDate >= oneWeekAgo;
  });

  const thisMonthComplaints = solvedComplaints.filter(c => {
    if (!c.resolvedAt && !c.updatedAt) return false;
    const resolvedDate = new Date(c.resolvedAt || c.updatedAt);
    const today = new Date();
    const oneMonthAgo = new Date(today.getFullYear(), today.getMonth() - 1, today.getDate());
    return resolvedDate >= oneMonthAgo;
  });

  const handleViewLocation = (latitude, longitude, title) => {
    setSelectedLocation({ lat: latitude, lng: longitude, title });
    setMapCenter([latitude, longitude]);
    setMapZoom(15);
    setShowMap(true);
  };

  const handleCloseMap = () => {
    setShowMap(false);
    setSelectedLocation(null);
  };

  // Show loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
              <p className="text-gray-500">Loading complaints...</p>
            </div>
          </div>
        </main>
      </div>
    );
  }

  // Enhanced complaint card rendering with better data handling
  const renderActiveComplaintCards = (complaintsToRender) => {
    if (complaintsToRender.length === 0) {
      return (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No complaints</h3>
          <p className="mt-1 text-sm text-gray-500">There are no active complaints at this time.</p>
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 animate-fadeIn">
        {complaintsToRender.map((complaint, index) => (
          <div key={complaint._id || complaint.id || index} className="bg-white overflow-hidden shadow rounded-lg hover-lift">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg leading-6 font-medium text-gray-900 truncate">
                  {complaint.title || complaint.category || 'Complaint'}
                </h3>
                <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                  complaint.priority === 'High' ? 'bg-red-100 text-red-800' : 
                  complaint.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' : 
                  'bg-green-100 text-green-800'
                }`}>
                  {complaint.priority || 'Medium'}
                </span>
              </div>

              <div className="mt-2 max-w-xl text-sm text-gray-500">
                <p className="line-clamp-2">{complaint.description || 'No description provided'}</p>
              </div>

              <div className="mt-3 flex items-center text-sm text-gray-500">
                <span className="truncate">{complaint.category || 'General'}</span>
                <span className="mx-1">•</span>
                <span className="truncate">{complaint.address || complaint.location || 'Location not specified'}</span>
              </div>

              <div className="mt-4 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  <span>Status: </span>
                  <span className="font-medium text-gray-900 capitalize">{complaint.status || 'Pending'}</span>
                </div>
                <div className="text-sm text-gray-500">
                  <span>Created: </span>
                  <span>{new Date(complaint.createdAt).toLocaleDateString() || 'N/A'}</span>
                </div>
              </div>

              {/* Show image if available */}
              {complaint.image && (
                <div className="mt-3">
                  <img 
                    src={complaint.image} 
                    alt="Complaint" 
                    className="h-32 w-full object-cover rounded-md"
                  />
                </div>
              )}

              <div className="mt-4 flex flex-wrap gap-2">
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200">
                  View Details
                </button>
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-green-700 bg-green-100 hover:bg-green-200">
                  Update Status
                </button>
                {complaint.latitude && complaint.longitude && (
                  <button 
                    onClick={() => handleViewLocation(complaint.latitude, complaint.longitude, complaint.category)}
                    className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200"
                  >
                    📍 View Location
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderSolvedComplaintCards = (complaintsToRender) => {
    if (complaintsToRender.length === 0) {
      return (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No solved complaints</h3>
          <p className="mt-1 text-sm text-gray-500">There are no completed complaints at this time.</p>
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 animate-fadeIn">
        {complaintsToRender.map((complaint, index) => (
          <div key={complaint._id || complaint.id || index} className="bg-white overflow-hidden shadow rounded-lg hover-lift">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg leading-6 font-medium text-gray-900 truncate">
                  {complaint.title || complaint.category || 'Complaint'}
                </h3>
                <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                  Resolved ✅
                </span>
              </div>

              <div className="mt-2 max-w-xl text-sm text-gray-500">
                <p className="line-clamp-2">{complaint.description || 'No description provided'}</p>
              </div>

              <div className="mt-3 flex items-center text-sm text-gray-500">
                <span className="truncate">{complaint.category || 'General'}</span>
                <span className="mx-1">•</span>
                <span className="truncate">{complaint.address || complaint.location || 'Location not specified'}</span>
              </div>

              <div className="mt-4 grid grid-cols-2 gap-4 text-sm">
                <div>
                  <p className="text-gray-500">Created</p>
                  <p className="font-medium">{new Date(complaint.createdAt).toLocaleDateString()}</p>
                </div>
                <div>
                  <p className="text-gray-500">Resolved</p>
                  <p className="font-medium">{new Date(complaint.resolvedAt || complaint.updatedAt).toLocaleDateString()}</p>
                </div>
              </div>

              <div className="mt-4 flex flex-wrap gap-2">
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200">
                  View Details
                </button>
                {complaint.latitude && complaint.longitude && (
                  <button 
                    onClick={() => handleViewLocation(complaint.latitude, complaint.longitude, complaint.category)}
                    className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200"
                  >
                    📍 View Location
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  // Define tabs with counts
  const activeTabs = [
    {
      label: `All (${activeComplaints.length})`,
      content: renderActiveComplaintCards(activeComplaints)
    },
    {
      label: `Pending (${pendingComplaints.length})`,
      content: renderActiveComplaintCards(pendingComplaints)
    },
    {
      label: `Assigned (${assignedComplaints.length})`,
      content: renderActiveComplaintCards(assignedComplaints)
    },
    {
      label: `In Progress (${inProgressComplaints.length})`,
      content: renderActiveComplaintCards(inProgressComplaints)
    }
  ];

  const solvedTabs = [
    {
      label: `All (${solvedComplaints.length})`,
      content: renderSolvedComplaintCards(solvedComplaints)
    },
    {
      label: `This Week (${thisWeekComplaints.length})`,
      content: renderSolvedComplaintCards(thisWeekComplaints)
    },
    {
      label: `This Month (${thisMonthComplaints.length})`,
      content: renderSolvedComplaintCards(thisMonthComplaints)
    }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">

          {/* Map Modal */}
          {showMap && (
            <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
              <div className="bg-white p-4 rounded-lg max-w-4xl w-full mx-4">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium">
                    {selectedLocation ? `Location: ${selectedLocation.title || 'Complaint Location'}` : 'Complaint Location'}
                  </h3>
                  <button 
                    onClick={handleCloseMap}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>

                <div className="h-96 rounded-lg overflow-hidden">
                  <MapContainer 
                    center={mapCenter} 
                    zoom={mapZoom} 
                    scrollWheelZoom={true}
                    style={{ height: '100%', width: '100%' }}
                  >
                    <MapView center={mapCenter} zoom={mapZoom} />
                    <TileLayer
                      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {selectedLocation && (
                      <Marker position={[selectedLocation.lat, selectedLocation.lng]}>
                        <Popup>
                          <div>
                            <h3 className="font-bold">{selectedLocation.title}</h3>
                          </div>
                        </Popup>
                      </Marker>
                    )}
                  </MapContainer>
                </div>
              </div>
            </div>
          )}

          <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Complaints Dashboard</h1>
              <p className="mt-1 text-sm text-gray-500">
                Total: {activeComplaints.length + solvedComplaints.length} complaints
              </p>
            </div>
            <div className="mt-4 md:mt-0 flex space-x-3">
              <button 
                onClick={() => window.location.reload()} 
                className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
              >
                🔄 Refresh
              </button>
            </div>
          </div>

          {/* Main tab switcher for Active/Completed */}
          <div className="mb-6">
            <div className="border-b border-gray-200">
              <nav className="-mb-px flex space-x-8">
                <button
                  onClick={() => setActiveMainTab('active')}
                  className={`${
                    activeMainTab === 'active'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
                >
                  Active Complaints ({activeComplaints.length})
                </button>
                <button
                  onClick={() => setActiveMainTab('completed')}
                  className={`${
                    activeMainTab === 'completed'
                      ? 'border-blue-500 text-blue-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
                >
                  Completed Complaints ({solvedComplaints.length})
                </button>
              </nav>
            </div>
          </div>

          <div className="bg-white shadow overflow-hidden sm:rounded-lg">
            {activeMainTab === 'active' ? (
              <TabSwitcher tabs={activeTabs} />
            ) : (
              <TabSwitcher tabs={solvedTabs} />
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default Complaints;
