import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../components/Navbar';
import TabSwitcher from '../components/TabSwitcher';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { useMap } from 'react-leaflet';

// Fix for default marker icon issue in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Component to handle map view changes
function MapView({ center, zoom }) {
  const map = useMap();

  useEffect(() => {
    map.setView(center, zoom);
  }, [center, zoom, map]);

  return null;
}

function Complaints() {
  // State to track which main tab is active (Active or Completed)
  const [activeMainTab, setActiveMainTab] = useState('active');

  // Empty arrays for complaints (removing dummy data as requested)
  const [activeComplaints, setActiveComplaints] = useState([]);
  const [solvedComplaints, setSolvedComplaints] = useState([]);
  const [showMap, setShowMap] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [mapCenter, setMapCenter] = useState([20.5937, 78.9629]); // Default center of India
  const [mapZoom, setMapZoom] = useState(5);

  useEffect(() => {
    // Function to fetch complaints from the backend API
    const fetchComplaints = async () => {
      try {
        // Import the complaint service
        const { getAllComplaints } = await import('../services/complaintService');

        // Fetch all complaints from the backend
        const response = await getAllComplaints();

        if (response.success) {
          // Filter complaints by status
          const active = response.data.filter(c => c.status !== 'resolved');
          const solved = response.data.filter(c => c.status === 'resolved');

          setActiveComplaints(active);
          setSolvedComplaints(solved);
        }
      } catch (error) {
        console.error('Error fetching complaints:', error);
        // Fallback to localStorage if API fails
        const storedComplaints = JSON.parse(localStorage.getItem('complaints') || '[]');
        const active = storedComplaints.filter(c => c.status !== 'Resolved');
        const solved = storedComplaints.filter(c => c.status === 'Resolved');

        setActiveComplaints(active);
        setSolvedComplaints(solved);
      }
    };

    fetchComplaints();
  }, []);

  // Filter active complaints by status
  const pendingComplaints = activeComplaints.filter(c => c.status === 'Pending');
  const assignedComplaints = activeComplaints.filter(c => c.status === 'Assigned');
  const inProgressComplaints = activeComplaints.filter(c => c.status === 'In Progress');

  // Filter solved complaints by resolution time
  const thisWeekComplaints = solvedComplaints.filter(c => {
    const resolvedDate = new Date(c.resolvedAt);
    const today = new Date();
    const oneWeekAgo = new Date(today.getFullYear(), today.getMonth(), today.getDate() - 7);
    return resolvedDate >= oneWeekAgo;
  });

  const thisMonthComplaints = solvedComplaints.filter(c => {
    const resolvedDate = new Date(c.resolvedAt);
    const today = new Date();
    const oneMonthAgo = new Date(today.getFullYear(), today.getMonth() - 1, today.getDate());
    return resolvedDate >= oneMonthAgo;
  });

  const handleViewLocation = (lat, lng, title) => {
    setSelectedLocation({ lat, lng, title });
    setMapCenter([lat, lng]);
    setMapZoom(15);
    setShowMap(true);
  };

  const handleCloseMap = () => {
    setShowMap(false);
    setSelectedLocation(null);
  };

  // Function to render active complaint cards
  const renderActiveComplaintCards = (complaintsToRender) => {
    if (complaintsToRender.length === 0) {
      return (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No complaints</h3>
          <p className="mt-1 text-sm text-gray-500">There are no active complaints at this time.</p>
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 animate-fadeIn">
        {complaintsToRender.map(complaint => (
          <div key={complaint.id} className="bg-white overflow-hidden shadow rounded-lg hover-lift">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg leading-6 font-medium text-gray-900 truncate">{complaint.title || complaint.category}</h3>
                <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                  complaint.priority === 'High' ? 'bg-red-100 text-red-800' : 
                  complaint.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' : 
                  'bg-green-100 text-green-800'
                }`}>
                  {complaint.priority}
                </span>
              </div>
              <div className="mt-2 max-w-xl text-sm text-gray-500">
                <p className="truncate">{complaint.description}</p>
              </div>
              <div className="mt-3 flex items-center text-sm text-gray-500">
                <span className="truncate">{complaint.category}</span>
                <span className="mx-1">â€¢</span>
                <span>{complaint.address || complaint.location}</span>
              </div>
              <div className="mt-4 flex items-center justify-between">
                <div className="text-sm text-gray-500">
                  <span>Assigned to: </span>
                  <span className="font-medium text-gray-900">{complaint.assignedTo || 'Unassigned'}</span>
                </div>
                <div className="text-sm text-gray-500">
                  <span>Created: </span>
                  <span>{complaint.createdAt || new Date().toLocaleDateString()}</span>
                </div>
              </div>
              <div className="mt-4 flex flex-wrap gap-2">
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                  View Details
                </button>
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-green-700 bg-green-100 hover:bg-green-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500">
                  Update Status
                </button>
                {complaint.lat && complaint.lng && (
                  <button 
                    onClick={() => handleViewLocation(complaint.lat, complaint.lng, complaint.title || complaint.category)}
                    className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                  >
                    View on Map
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  // Function to render solved complaint cards
  const renderSolvedComplaintCards = (complaintsToRender) => {
    if (complaintsToRender.length === 0) {
      return (
        <div className="text-center py-12">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No complaints</h3>
          <p className="mt-1 text-sm text-gray-500">There are no completed complaints at this time.</p>
        </div>
      );
    }

    return (
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 animate-fadeIn">
        {complaintsToRender.map(complaint => (
          <div key={complaint.id} className="bg-white overflow-hidden shadow rounded-lg hover-lift">
            <div className="px-4 py-5 sm:p-6">
              <div className="flex items-center justify-between">
                <h3 className="text-lg leading-6 font-medium text-gray-900 truncate">{complaint.title || complaint.category}</h3>
                <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                  Resolved
                </span>
              </div>
              <div className="mt-2 max-w-xl text-sm text-gray-500">
                <p className="truncate">{complaint.description}</p>
              </div>
              <div className="mt-3 flex items-center text-sm text-gray-500">
                <span className="truncate">{complaint.category}</span>
                <span className="mx-1">â€¢</span>
                <span>{complaint.address || complaint.location}</span>
              </div>
              <div className="mt-4 grid grid-cols-2 gap-4 text-sm">
                <div>
                  <p className="text-gray-500">Created</p>
                  <p className="font-medium">{complaint.createdAt}</p>
                </div>
                <div>
                  <p className="text-gray-500">Resolved</p>
                  <p className="font-medium">{complaint.resolvedAt}</p>
                </div>
                <div className="col-span-2">
                  <p className="text-gray-500">Resolved by</p>
                  <p className="font-medium">{complaint.resolvedBy}</p>
                </div>
              </div>
              <div className="mt-4 flex flex-wrap gap-2">
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                  View Details
                </button>
                <button className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                  Download Report
                </button>
                {complaint.lat && complaint.lng && (
                  <button 
                    onClick={() => handleViewLocation(complaint.lat, complaint.lng, complaint.title || complaint.category)}
                    className="inline-flex items-center px-2.5 py-1.5 border border-transparent text-xs font-medium rounded text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                  >
                    View on Map
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  // Define tabs for active complaints
  const activeTabs = [
    {
      label: 'All',
      content: renderActiveComplaintCards(activeComplaints)
    },
    {
      label: 'Pending',
      content: renderActiveComplaintCards(pendingComplaints)
    },
    {
      label: 'Assigned',
      content: renderActiveComplaintCards(assignedComplaints)
    },
    {
      label: 'In Progress',
      content: renderActiveComplaintCards(inProgressComplaints)
    }
  ];

  // Define tabs for solved complaints
  const solvedTabs = [
    {
      label: 'All',
      content: renderSolvedComplaintCards(solvedComplaints)
    },
    {
      label: 'This Week',
      content: renderSolvedComplaintCards(thisWeekComplaints)
    },
    {
      label: 'This Month',
      content: renderSolvedComplaintCards(thisMonthComplaints)
    }
  ];

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          {/* Map Modal */}
          {showMap && (
            <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
              <div className="bg-white p-4 rounded-lg max-w-4xl w-full">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium">
                    {selectedLocation ? `Location: ${selectedLocation.title || 'Complaint Location'}` : 'Complaint Location'}
                  </h3>
                  <button 
                    onClick={handleCloseMap}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>

                <div className="h-96 rounded-lg overflow-hidden">
                  <MapContainer 
                    center={[20.5937, 78.9629]} 
                    zoom={5} 
                    scrollWheelZoom={true}
                    style={{ height: '100%', width: '100%' }}
                  >
                    <MapView center={mapCenter} zoom={mapZoom} />
                    <TileLayer
                      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {selectedLocation && (
                      <Marker 
                        position={[selectedLocation.lat, selectedLocation.lng]}
                      >
                        <Popup>
                          <div>
                            <h3 className="font-bold">{selectedLocation.title}</h3>
                            <p>{selectedLocation.address || selectedLocation.location}</p>
                          </div>
                        </Popup>
                      </Marker>
                    )}
                  </MapContainer>
                </div>
              </div>
            </div>
          )}

          <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Complaints</h1>
              <p className="mt-1 text-sm text-gray-500">Manage and track all complaints in the system</p>
            </div>
            <div className="mt-4 md:mt-0 flex space-x-3">
              <div className="relative">
                <input
                  type="text"
                  placeholder="Search complaints..."
                  className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <svg className="h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                    <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
                  </svg>
                </div>
              </div>
              <button className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
                {activeMainTab === 'active' ? 'Filter' : 'Export'}
              </button>
            </div>
          </div>

          {/* Main tab switcher for Active/Completed */}
          <div className="mb-6">
            <div className="sm:hidden">
              <select
                id="tabs"
                name="tabs"
                className="block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
                value={activeMainTab}
                onChange={(e) => setActiveMainTab(e.target.value)}
              >
                <option value="active">Active Complaints</option>
                <option value="completed">Completed Complaints</option>
              </select>
            </div>
            <div className="hidden sm:block">
              <div className="border-b border-gray-200">
                <nav className="-mb-px flex space-x-8" aria-label="Tabs">
                  <button
                    onClick={() => setActiveMainTab('active')}
                    className={`${
                      activeMainTab === 'active'
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
                  >
                    Active Complaints
                  </button>
                  <button
                    onClick={() => setActiveMainTab('completed')}
                    className={`${
                      activeMainTab === 'completed'
                        ? 'border-blue-500 text-blue-600'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
                  >
                    Completed Complaints
                  </button>
                </nav>
              </div>
            </div>
          </div>

          <div className="bg-white shadow overflow-hidden sm:rounded-lg">
            {activeMainTab === 'active' ? (
              <TabSwitcher tabs={activeTabs} />
            ) : (
              <TabSwitcher tabs={solvedTabs} />
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default Complaints;
