import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

// inside your component:
useEffect(() => {
  // When login page mounts, clear stored session
  localStorage.removeItem("user");
}, []);

export default function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const res = await axios.post("http://localhost:5000/api/auth/login", {
      email,
      password,
    });

    if (res.data.success) {
      // Save user/token to localStorage or context
      localStorage.setItem("user", JSON.stringify(res.data.user));
      navigate("/dashboard");
    } else {
      alert(res.data.message || "Login failed");
    }
  } catch (err) {
    alert("Login error: " + err.response?.data?.message || err.message);
  }
};

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 px-4">
      <div className="bg-white rounded-md shadow-md w-full max-w-4xl overflow-hidden grid grid-cols-1 md:grid-cols-2">
        {/* Left: Form */}
        <div className="p-8 space-y-6">
          <div className="text-center">
            <h2 className="text-2xl font-bold">Welcome back</h2>
            <p className="text-sm text-gray-600">Login to your account</p>
          </div>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email
              </label>
              <input
                type="email"
                id="email"
                className="w-full mt-1 p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="m@example.com"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div>
              <div className="flex justify-between items-center">
                <label htmlFor="password" className="text-sm font-medium text-gray-700">
                  Password
                </label>
                <a href="#" className="text-sm text-blue-500 hover:underline">
                  Forgot your password?
                </a>
              </div>
              <input
                type="password"
                id="password"
                className="w-full mt-1 p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            <button
              type="submit"
              className="w-full bg-black text-white py-2 rounded-md hover:bg-gray-800 transition"
            >
              Login
            </button>
          </form>

          <div className="text-center text-sm text-gray-600">
            Donâ€™t have an account?{" "}
            <button
              onClick={() => navigate("/register")}
              className="text-blue-500 underline"
            >
              Sign up
            </button>
          </div>
        </div> 

        {/* Right: Image/Design */}
        <div className="hidden md:block bg-gray-200 relative">
          <img
            src="/placeholder.svg"
            alt="Login Illustration"
            className="absolute inset-0 w-full h-full object-cover"
          />
        </div>
      </div>

      <p className="text-xs text-gray-500 mt-6 text-center">
        By clicking continue, you agree to our{" "}
        <a href="#" className="underline">
          Terms of Service
        </a>{" "}
        and{" "}
        <a href="#" className="underline">
          Privacy Policy
        </a>
        .
      </p>
    </div>
  );
}
