import { useEffect, useRef, useState } from 'react';

export default function CameraTest() {
  const videoRef   = useRef(null);
  const canvasRef  = useRef(null);
  const [thumb, setThumb] = useState(null);
  const [stream, setStream] = useState(null);

  /* â”€â”€â”€â”€â”€ open camera on mount â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  useEffect(() => {
    (async () => {
      try {
        const s = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: 'environment' },
          audio: false,
        });
        setStream(s);
        videoRef.current.srcObject = s;
        await videoRef.current.play();
      } catch (err) {
        alert(`Camera error: ${err.name}`);
        console.error(err);
      }
    })();

    /* cleanup on unmount */
    return () => stream?.getTracks().forEach((t) => t.stop());
  }, []);                        //  â† empty deps on purpose

  /* â”€â”€â”€â”€â”€ capture frame â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const snap = () => {
    const v = videoRef.current;
    const c = canvasRef.current;
    if (!v || !c || v.videoWidth === 0) return alert('Video not ready');

    c.width  = v.videoWidth;
    c.height = v.videoHeight;
    c.getContext('2d').drawImage(v, 0, 0, c.width, c.height);

    const dataUrl = c.toDataURL('image/jpeg', 0.8);
    setThumb(dataUrl);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center gap-6 bg-gray-100 p-6">
      <h1 className="text-xl font-semibold">ðŸ“¸ Camera-only test page</h1>

      <video
        ref={videoRef}
        className="w-full max-w-xs rounded border"
        playsInline
        muted
        autoPlay
      />

      <canvas ref={canvasRef} className="hidden" />

      <button
        onClick={snap}
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Capture frame
      </button>

      {thumb && (
        <img
          src={thumb}
          alt="thumbnail"
          className="max-h-40 rounded border shadow"
        />
      )}
    </div>
  );
}
