import React, { useState } from 'react';

function RegisterComplaint() {
  const [category, setCategory] = useState('Garbage');
  const [description, setDescription] = useState('');
  const [location, setLocation] = useState({ lat: '', lng: '' });
  const [image, setImage] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ category, description, location, image });
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Register a Complaint</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <label>Category:
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="Garbage">Garbage</option>
            <option value="Water Leakage">Water Leakage</option>
            <option value="Pothole">Pothole</option>
            <option value="Electricity Issue">Electricity Issue</option>
            <option value="Others">Others</option>
          </select>
        </label>

        <label>Description:
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </label>

        <label>Latitude:
          <input type="number" value={location.lat} onChange={(e) => setLocation({ ...location, lat: e.target.value })} required />
        </label>

        <label>Longitude:
          <input type="number" value={location.lng} onChange={(e) => setLocation({ ...location, lng: e.target.value })} required />
        </label>

        <label>Upload Image:
          <input type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        </label>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

const styles = {
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
    maxWidth: '400px'
  }
};

export default RegisterComplaint;
