import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [activeTab, setActiveTab] = useState('volunteer');
  const navigate = useNavigate();

  const renderTabContent = () => {
    switch (activeTab) {
      case 'volunteer':
        return <p>[Volunteer form will go here]</p>;
      case 'active':
        return <p>[Active complaints list will go here]</p>;
      case 'solved':
        return <p>[Solved complaints list will go here]</p>;
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Welcome to Complaint Tracker Dashboard</h2>

      <div style={styles.buttonRow}>
        <button onClick={() => navigate('/complaint')}>üìù Register Complaint</button>
        <button onClick={() => setActiveTab('volunteer')}>ü§ù Become Volunteer</button>
        <button onClick={() => setActiveTab('active')}>üü† Active Complaints</button>
        <button onClick={() => setActiveTab('solved')}>‚úÖ Solved Complaints</button>
      </div>

      <div style={{ marginTop: '2rem' }}>
        {renderTabContent()}
      </div>
    </div>
  );
}

const styles = {
  buttonRow: {
    display: 'flex',
    gap: '1rem',
    flexWrap: 'wrap',
    marginTop: '1rem',
    marginBottom: '1rem'
  }
};

export default Dashboard;
