import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import TabSwitcher from '../components/TabSwitcher';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../api';

// Fix for default marker icon issue in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom group marker icon
const createGroupIcon = (memberCount, isOwner) => {
  const color = isOwner ? '#dc2626' : '#059669'; // Red for owner, green for member

  return L.divIcon({
    className: 'group-marker',
    html: `
      <div style="
        background-color: ${color};
        width: 28px;
        height: 28px;
        border-radius: 50%;
        border: 3px solid white;
        box-shadow: 0 2px 6px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        font-size: 10px;
        font-weight: bold;
      ">${memberCount}</div>
    `,
    iconSize: [28, 28],
    iconAnchor: [14, 14]
  });
};

function Volunteer() {
  const [activeTab, setActiveTab] = useState('browse-groups');
  const [complaints, setComplaints] = useState([]);
  const [volunteerGroups, setVolunteerGroups] = useState([]);
  const [myGroups, setMyGroups] = useState([]);
  const [joinedGroups, setJoinedGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('Getting location...');

  // Modal states
  const [showCreateGroupModal, setShowCreateGroupModal] = useState(false);
  const [showJoinGroupModal, setShowJoinGroupModal] = useState(false);
  const [showGroupDetailsModal, setShowGroupDetailsModal] = useState(false);
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);

  // Form states
  const [groupForm, setGroupForm] = useState({
    name: '',
    description: '',
    maxMembers: 5,
    skills: '',
    complaintId: null
  });

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    setCurrentUser(user);

    fetchComplaints();
    fetchVolunteerGroups();
    getCurrentUserLocation();
  }, []);

  const getCurrentUserLocation = () => {
    setLocationStatus('Getting your location...');
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(location);
          setLocationStatus(`Location found: ${location.lat.toFixed(4)}, ${location.lng.toFixed(4)}`);
        },
        (error) => {
          console.error('Error getting user location:', error);
          setLocationStatus('Location access denied. Enable location to see nearby groups.');
        }
      );
    } else {
      setLocationStatus('Geolocation not supported by this browser');
    }
  };

  const fetchComplaints = async () => {
    try {
      const response = await api.get('/api/complaints');
      const allComplaints = response.data.data || response.data;

      // Filter active complaints that can have volunteer groups
      const activeComplaints = allComplaints.filter(c =>
        c.status !== 'completed' && c.status !== 'resolved' &&
        c.latitude && c.longitude
      );

      setComplaints(activeComplaints);

    } catch (error) {
      console.error('Error fetching complaints:', error);
      const storedComplaints = JSON.parse(localStorage.getItem('complaints') || '[]');
      setComplaints(storedComplaints.filter(c => c.latitude && c.longitude));
    } finally {
      setLoading(false);
    }
  };

  const fetchVolunteerGroups = async () => {
    try {
      console.log('🔍 Fetching volunteer groups...');
      const response = await api.get('/api/volunteer-groups');
      const allGroups = response.data.data || response.data;

      console.log('📊 Raw API response groups:', allGroups.length);

      // Log each group's membership for debugging
      allGroups.forEach(g => {
        const isMyGroup = g.createdBy === currentUser?._id;
        const amIMember = g.members && g.members.some(m => m.userId === currentUser?._id);
        console.log(`Group "${g.name}":`, {
          createdBy: g.createdBy,
          currentUserId: currentUser?._id,
          isMyGroup,
          amIMember,
          memberUserIds: g.members ? g.members.map(m => m.userId) : [],
          memberCount: g.members ? g.members.length : 0
        });
      });

      // FIXED: Use fresh current user data for each categorization
      const freshUser = JSON.parse(localStorage.getItem('user') || '{}');

      // Separate groups with fresh user data
      const myGroups = allGroups.filter(g => g.createdBy === freshUser._id);

      const joinedGroups = allGroups.filter(g =>
        g.members &&
        g.members.some(m => m.userId === freshUser._id) &&
        g.createdBy !== freshUser._id
      );

      console.log('📈 Groups categorized with fresh user data:', {
        total: allGroups.length,
        myGroups: myGroups.length,
        joinedGroups: joinedGroups.length,
        freshUserId: freshUser._id
      });

      // Update all states
      setVolunteerGroups(allGroups);
      setMyGroups(myGroups);
      setJoinedGroups(joinedGroups);

    } catch (error) {
      console.error('❌ Error fetching volunteer groups:', error);
      const storedGroups = JSON.parse(localStorage.getItem('volunteer_groups') || '[]');
      setVolunteerGroups(storedGroups);
    }
  };



  const handleCreateGroup = async () => {
    if (!groupForm.name.trim()) {
      alert('Please enter a group name');
      return;
    }

    if (!selectedComplaint) {
      alert('Please select a complaint to create a group for');
      return;
    }

    try {
      const groupData = {
        name: groupForm.name,
        description: groupForm.description,
        maxMembers: parseInt(groupForm.maxMembers),
        requiredSkills: groupForm.skills.split(',').map(s => s.trim()).filter(s => s),
        complaintId: selectedComplaint._id,
        complaintTitle: selectedComplaint.category,
        complaintLocation: {
          address: selectedComplaint.address,
          latitude: selectedComplaint.latitude,
          longitude: selectedComplaint.longitude
        },
        createdBy: currentUser._id,
        createdByUsername: currentUser.username || currentUser.email,
        status: 'open',
        members: [{
          userId: currentUser._id,
          username: currentUser.username || currentUser.email,
          role: 'leader',
          joinedAt: new Date().toISOString()
        }]
      };

      console.log('Creating volunteer group:', groupData);

      const response = await api.post('/api/volunteer-groups', groupData);

      if (response.data.success) {
        alert('✅ Volunteer group created successfully!');
        fetchVolunteerGroups();
        setShowCreateGroupModal(false);
        setGroupForm({
          name: '',
          description: '',
          maxMembers: 5,
          skills: '',
          complaintId: null
        });
        setSelectedComplaint(null);
      }

    } catch (error) {
      console.error('Error creating volunteer group:', error);
      alert('❌ Failed to create volunteer group: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleJoinGroup = async (group) => {
    if (group.members && group.members.length >= group.maxMembers) {
      alert('This group is already full');
      return;
    }

    if (group.members && group.members.some(m => m.userId === currentUser._id)) {
      alert('You are already a member of this group');
      return;
    }

    try {
      const joinData = {
        userId: currentUser._id,
        username: currentUser.username || currentUser.email
      };

      console.log(`Joining group ${group._id}:`, joinData);

      const response = await api.post(`/api/volunteer-groups/${group._id}/join`, joinData);

      if (response.data.success) {
        alert('✅ Successfully joined the volunteer group!');
        fetchVolunteerGroups();
      }

    } catch (error) {
      console.error('Error joining volunteer group:', error);
      alert('❌ Failed to join volunteer group: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLeaveGroup = async (group) => {
    if (group.createdBy === currentUser._id) {
      alert('As the group leader, you cannot leave the group. You can delete it instead.');
      return;
    }

    try {
      console.log('🚪 Leaving group:', group.name, 'User ID:', currentUser._id);

      const response = await api.post(`/api/volunteer-groups/${group._id}/leave`, {
        userId: currentUser._id
      });

      if (response.data.success) {
        console.log('✅ Successfully left group via API');

        // CRITICAL: Immediately update the specific group in state
        setVolunteerGroups(prevGroups =>
          prevGroups.map(g => {
            if (g._id === group._id) {
              // Remove current user from members array
              const updatedMembers = g.members.filter(m => m.userId !== currentUser._id);
              console.log(`👥 Updated members for "${g.name}":`, updatedMembers.map(m => m.username));
              return { ...g, members: updatedMembers };
            }
            return g;
          })
        );

        // Also update joinedGroups state immediately
        setJoinedGroups(prevJoined =>
          prevJoined.filter(g => g._id !== group._id)
        );

        // Force a complete refresh to ensure consistency
        setTimeout(async () => {
          await fetchVolunteerGroups();
          console.log('🔄 Complete refresh after leave operation');
        }, 100);

        alert('✅ You have left the volunteer group');
      }

    } catch (error) {
      console.error('❌ Error leaving volunteer group:', error);
      alert('❌ Failed to leave volunteer group: ' + (error.response?.data?.message || error.message));
    }
  };





const handleDeleteGroup = async (group) => {
  if (group.createdBy !== currentUser._id) {
    alert('Only the group leader can delete this group');
    return;
  }

  if (window.confirm(`Are you sure you want to delete the group "${group.name}"? This action cannot be undone.`)) {
    try {
      console.log('🗑️ Deleting group:', group.name, 'ID:', group._id);

      const response = await api.delete(`/api/volunteer-groups/${group._id}`);

      if (response.data.success) {
        console.log('✅ Group deleted successfully from API');

        // CRITICAL: Clear ALL states immediately
        setVolunteerGroups([]);
        setMyGroups([]);
        setJoinedGroups([]);
        setSelectedGroup(null);

        // Clear any localStorage cache that might be stale
        localStorage.removeItem('volunteer_groups');

        // Force fresh data fetch after short delay
        setTimeout(async () => {
          await fetchVolunteerGroups();
          console.log('🔄 Complete refresh after group deletion');
        }, 200);

        alert('✅ Volunteer group deleted successfully');
      }

    } catch (error) {
      console.error('❌ Error deleting volunteer group:', error);
      alert('❌ Failed to delete volunteer group: ' + (error.response?.data?.message || error.message));
    }
  }
};


  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const renderBrowseGroups = () => {
    // FIXED: Use fresh user data and real-time filtering
    const freshUser = JSON.parse(localStorage.getItem('user') || '{}');

    const availableGroups = volunteerGroups.filter(g => {
      console.log(`🔍 Filtering group "${g.name}":`, {
        status: g.status,
        createdBy: g.createdBy,
        freshUserId: freshUser._id,
        isMyGroup: g.createdBy === freshUser._id,
        members: g.members ? g.members.map(m => ({ userId: m.userId, username: m.username })) : [],
        amIMemberNow: g.members ? g.members.some(m => m.userId === freshUser._id) : false
      });

      // Only show open groups
      if (g.status !== 'open') {
        console.log(`❌ Group "${g.name}" excluded: status is ${g.status}`);
        return false;
      }

      // Don't show groups you created
      if (g.createdBy === freshUser._id) {
        console.log(`❌ Group "${g.name}" excluded: you created it`);
        return false;
      }

      // CRITICAL: Don't show groups where you're currently a member
      const isCurrentMember = g.members && g.members.some(m => m.userId === freshUser._id);
      if (isCurrentMember) {
        console.log(`❌ Group "${g.name}" excluded: you're still a member`);
        return false;
      }

      console.log(`✅ Group "${g.name}" included: available to join`);
      return true;
    });

    console.log(`📋 Final available groups count: ${availableGroups.length}`);

    if (availableGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">👥</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Available Groups</h3>
          <p className="text-gray-600 mb-4">
            There are no volunteer groups available to join right now.
          </p>
          <div className="space-y-2">
            <button
              onClick={() => setShowCreateGroupModal(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Create Your Own Group
            </button>
            <br />
            <button
              onClick={() => fetchVolunteerGroups()}
              className="px-3 py-1 bg-gray-500 text-white text-sm rounded"
            >
              🔄 Refresh Groups
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            Available Volunteer Groups ({availableGroups.length})
          </h3>
          <button
            onClick={() => setShowCreateGroupModal(true)}
            className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 text-sm"
          >
            + Create New Group
          </button>
        </div>

        {availableGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={freshUser} // Use fresh user data
            onJoin={() => handleJoinGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            showActions={true}
          />
        ))}
      </div>
    );
  };




  const renderMyGroups = () => {
    if (myGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">👤</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Groups Created</h3>
          <p className="text-gray-600 mb-4">
            You haven't created any volunteer groups yet.
          </p>
          <button
            onClick={() => setShowCreateGroupModal(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Create Your First Group
          </button>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {myGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={currentUser}
            onDelete={() => handleDeleteGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            isOwner={true}
          />
        ))}
      </div>
    );
  };

  const renderJoinedGroups = () => {
    // FIXED: Use fresh user data to determine joined groups
    const freshUser = JSON.parse(localStorage.getItem('user') || '{}');

    // Filter joined groups using current server data
    const currentJoinedGroups = volunteerGroups.filter(g => {
      const isJoined = g.members &&
        g.members.some(m => m.userId === freshUser._id) &&
        g.createdBy !== freshUser._id;

      console.log(`Checking joined status for "${g.name}":`, {
        groupId: g._id,
        members: g.members ? g.members.map(m => m.username) : [],
        memberUserIds: g.members ? g.members.map(m => m.userId) : [],
        freshUserId: freshUser._id,
        isJoined: isJoined
      });

      return isJoined;
    });

    console.log('📋 Current joined groups:', currentJoinedGroups.length);

    if (currentJoinedGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">🤝</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Groups Joined</h3>
          <p className="text-gray-600">
            You haven't joined any volunteer groups yet. Browse available groups to get started!
          </p>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {currentJoinedGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={freshUser} // Use fresh user data
            onLeave={() => handleLeaveGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            isMember={true}
          />
        ))}
      </div>
    );
  };

  const renderGroupMap = () => {
    if (!userLocation || volunteerGroups.length === 0) {
      return (
        <div className="flex items-center justify-center h-96 bg-gray-50 rounded-lg">
          <div className="text-center">
            <div className="mb-4">🗺️</div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">Map Not Available</h3>
            <p className="text-gray-600">
              {!userLocation ? 'Location access required to show map' : 'No volunteer groups to display'}
            </p>
          </div>
        </div>
      );
    }

    return (
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div style={{ height: '500px', width: '100%' }}>
          <MapContainer
            center={[userLocation.lat, userLocation.lng]}
            zoom={12}
            scrollWheelZoom={true}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

