import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import TabSwitcher from '../components/TabSwitcher';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../api';

// Fix for default marker icon issue in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom group marker icon
const createGroupIcon = (memberCount, isOwner) => {
  const color = isOwner ? '#dc2626' : '#059669'; // Red for owner, green for member

  return L.divIcon({
    className: 'group-marker',
    html: `
      <div style="
        background-color: ${color};
        width: 28px;
        height: 28px;
        border-radius: 50%;
        border: 3px solid white;
        box-shadow: 0 2px 6px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        font-size: 10px;
        font-weight: bold;
      ">${memberCount}</div>
    `,
    iconSize: [28, 28],
    iconAnchor: [14, 14]
  });
};

function Volunteer() {
  const [activeTab, setActiveTab] = useState('browse-groups');
  const [complaints, setComplaints] = useState([]);
  const [volunteerGroups, setVolunteerGroups] = useState([]);
  const [myGroups, setMyGroups] = useState([]);
  const [joinedGroups, setJoinedGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('Getting location...');

  // Modal states
  const [showCreateGroupModal, setShowCreateGroupModal] = useState(false);
  const [showJoinGroupModal, setShowJoinGroupModal] = useState(false);
  const [showGroupDetailsModal, setShowGroupDetailsModal] = useState(false);
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [selectedGroup, setSelectedGroup] = useState(null);

  // Form states
  const [groupForm, setGroupForm] = useState({
    name: '',
    description: '',
    maxMembers: 5,
    skills: '',
    complaintId: null
  });

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    setCurrentUser(user);

    fetchComplaints();
    fetchVolunteerGroups();
    getCurrentUserLocation();
  }, []);

  const getCurrentUserLocation = () => {
    setLocationStatus('Getting your location...');
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setUserLocation(location);
          setLocationStatus(`Location found: ${location.lat.toFixed(4)}, ${location.lng.toFixed(4)}`);
        },
        (error) => {
          console.error('Error getting user location:', error);
          setLocationStatus('Location access denied. Enable location to see nearby groups.');
        }
      );
    } else {
      setLocationStatus('Geolocation not supported by this browser');
    }
  };

  const fetchComplaints = async () => {
    try {
      const response = await api.get('/api/complaints');
      const allComplaints = response.data.data || response.data;

      // Filter active complaints that can have volunteer groups
      const activeComplaints = allComplaints.filter(c =>
        c.status !== 'completed' && c.status !== 'resolved' &&
        c.latitude && c.longitude
      );

      setComplaints(activeComplaints);

    } catch (error) {
      console.error('Error fetching complaints:', error);
      const storedComplaints = JSON.parse(localStorage.getItem('complaints') || '[]');
      setComplaints(storedComplaints.filter(c => c.latitude && c.longitude));
    } finally {
      setLoading(false);
    }
  };

  const fetchVolunteerGroups = async () => {
    try {
      console.log('🔍 Fetching volunteer groups...');
      const response = await api.get('/api/volunteer-groups');
      const allGroups = response.data.data || response.data;

      console.log('📊 Raw API response groups:', allGroups.length);

      // Log each group's membership for debugging
      allGroups.forEach(g => {
        const isMyGroup = g.createdBy === currentUser?._id;
        const amIMember = g.members && g.members.some(m => m.userId === currentUser?._id);
        console.log(`Group "${g.name}":`, {
          createdBy: g.createdBy,
          currentUserId: currentUser?._id,
          isMyGroup,
          amIMember,
          memberUserIds: g.members ? g.members.map(m => m.userId) : [],
          memberCount: g.members ? g.members.length : 0
        });
      });

      // FIXED: Use fresh current user data for each categorization
      const freshUser = JSON.parse(localStorage.getItem('user') || '{}');

      // Separate groups with fresh user data
      const myGroups = allGroups.filter(g => g.createdBy === freshUser._id);

      const joinedGroups = allGroups.filter(g =>
        g.members &&
        g.members.some(m => m.userId === freshUser._id) &&
        g.createdBy !== freshUser._id
      );

      console.log('📈 Groups categorized with fresh user data:', {
        total: allGroups.length,
        myGroups: myGroups.length,
        joinedGroups: joinedGroups.length,
        freshUserId: freshUser._id
      });

      // Update all states
      setVolunteerGroups(allGroups);
      setMyGroups(myGroups);
      setJoinedGroups(joinedGroups);

    } catch (error) {
      console.error('❌ Error fetching volunteer groups:', error);
      const storedGroups = JSON.parse(localStorage.getItem('volunteer_groups') || '[]');
      setVolunteerGroups(storedGroups);
    }
  };



  const handleCreateGroup = async () => {
    if (!groupForm.name.trim()) {
      alert('Please enter a group name');
      return;
    }

    if (!selectedComplaint) {
      alert('Please select a complaint to create a group for');
      return;
    }

    try {
      const groupData = {
        name: groupForm.name,
        description: groupForm.description,
        maxMembers: parseInt(groupForm.maxMembers),
        requiredSkills: groupForm.skills.split(',').map(s => s.trim()).filter(s => s),
        complaintId: selectedComplaint._id,
        complaintTitle: selectedComplaint.category,
        complaintLocation: {
          address: selectedComplaint.address,
          latitude: selectedComplaint.latitude,
          longitude: selectedComplaint.longitude
        },
        createdBy: currentUser._id,
        createdByUsername: currentUser.username || currentUser.email,
        status: 'open',
        members: [{
          userId: currentUser._id,
          username: currentUser.username || currentUser.email,
          role: 'leader',
          joinedAt: new Date().toISOString()
        }]
      };

      console.log('Creating volunteer group:', groupData);

      const response = await api.post('/api/volunteer-groups', groupData);

      if (response.data.success) {
        alert('✅ Volunteer group created successfully!');
        fetchVolunteerGroups();
        setShowCreateGroupModal(false);
        setGroupForm({
          name: '',
          description: '',
          maxMembers: 5,
          skills: '',
          complaintId: null
        });
        setSelectedComplaint(null);
      }

    } catch (error) {
      console.error('Error creating volunteer group:', error);
      alert('❌ Failed to create volunteer group: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleJoinGroup = async (group) => {
    if (group.members && group.members.length >= group.maxMembers) {
      alert('This group is already full');
      return;
    }

    if (group.members && group.members.some(m => m.userId === currentUser._id)) {
      alert('You are already a member of this group');
      return;
    }

    try {
      const joinData = {
        userId: currentUser._id,
        username: currentUser.username || currentUser.email
      };

      console.log(`Joining group ${group._id}:`, joinData);

      const response = await api.post(`/api/volunteer-groups/${group._id}/join`, joinData);

      if (response.data.success) {
        alert('✅ Successfully joined the volunteer group!');
        fetchVolunteerGroups();
      }

    } catch (error) {
      console.error('Error joining volunteer group:', error);
      alert('❌ Failed to join volunteer group: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLeaveGroup = async (group) => {
    if (group.createdBy === currentUser._id) {
      alert('As the group leader, you cannot leave the group. You can delete it instead.');
      return;
    }

    try {
      console.log('🚪 Leaving group:', group.name, 'Current user ID:', currentUser._id);

      // Show loading state
      setLoading(true);

      const response = await api.post(`/api/volunteer-groups/${group._id}/leave`, {
        userId: currentUser._id
      });

      if (response.data.success) {
        console.log('✅ Successfully left group via API');

        // CRITICAL: Clear ALL related states first
        setVolunteerGroups([]);
        setMyGroups([]);
        setJoinedGroups([]);

        // Wait a bit for state to clear
        await new Promise(resolve => setTimeout(resolve, 100));

        // Force fresh data fetch
        await fetchVolunteerGroups();

        // Verify the updated state
        console.log('🔍 Post-leave verification...');

        alert('✅ You have left the volunteer group');
      }

    } catch (error) {
      console.error('❌ Error leaving volunteer group:', error);
      alert('❌ Failed to leave volunteer group: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };





  const handleDeleteGroup = async (group) => {
    if (group.createdBy !== currentUser._id) {
      alert('Only the group leader can delete this group');
      return;
    }

    if (window.confirm(`Are you sure you want to delete the group "${group.name}"? This action cannot be undone.`)) {
      try {
        const response = await api.delete(`/api/volunteer-groups/${group._id}`);

        if (response.data.success) {
          alert('✅ Volunteer group deleted successfully');
          fetchVolunteerGroups();
        }

      } catch (error) {
        console.error('Error deleting volunteer group:', error);
        alert('❌ Failed to delete volunteer group: ' + (error.response?.data?.message || error.message));
      }
    }
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const renderBrowseGroups = () => {
    // FIXED: Use fresh user data and real-time filtering
    const freshUser = JSON.parse(localStorage.getItem('user') || '{}');

    const availableGroups = volunteerGroups.filter(g => {
      console.log(`🔍 Filtering group "${g.name}":`, {
        status: g.status,
        createdBy: g.createdBy,
        freshUserId: freshUser._id,
        isMyGroup: g.createdBy === freshUser._id,
        members: g.members ? g.members.map(m => ({ userId: m.userId, username: m.username })) : [],
        amIMemberNow: g.members ? g.members.some(m => m.userId === freshUser._id) : false
      });

      // Only show open groups
      if (g.status !== 'open') {
        console.log(`❌ Group "${g.name}" excluded: status is ${g.status}`);
        return false;
      }

      // Don't show groups you created
      if (g.createdBy === freshUser._id) {
        console.log(`❌ Group "${g.name}" excluded: you created it`);
        return false;
      }

      // CRITICAL: Don't show groups where you're currently a member
      const isCurrentMember = g.members && g.members.some(m => m.userId === freshUser._id);
      if (isCurrentMember) {
        console.log(`❌ Group "${g.name}" excluded: you're still a member`);
        return false;
      }

      console.log(`✅ Group "${g.name}" included: available to join`);
      return true;
    });

    console.log(`📋 Final available groups count: ${availableGroups.length}`);

    if (availableGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">👥</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Available Groups</h3>
          <p className="text-gray-600 mb-4">
            There are no volunteer groups available to join right now.
          </p>
          <div className="space-y-2">
            <button
              onClick={() => setShowCreateGroupModal(true)}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Create Your Own Group
            </button>
            <br />
            <button
              onClick={() => fetchVolunteerGroups()}
              className="px-3 py-1 bg-gray-500 text-white text-sm rounded"
            >
              🔄 Refresh Groups
            </button>
          </div>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            Available Volunteer Groups ({availableGroups.length})
          </h3>
          <button
            onClick={() => setShowCreateGroupModal(true)}
            className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 text-sm"
          >
            + Create New Group
          </button>
        </div>

        {availableGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={freshUser} // Use fresh user data
            onJoin={() => handleJoinGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            showActions={true}
          />
        ))}
      </div>
    );
  };




  const renderMyGroups = () => {
    if (myGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">👤</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Groups Created</h3>
          <p className="text-gray-600 mb-4">
            You haven't created any volunteer groups yet.
          </p>
          <button
            onClick={() => setShowCreateGroupModal(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Create Your First Group
          </button>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {myGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={currentUser}
            onDelete={() => handleDeleteGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            isOwner={true}
          />
        ))}
      </div>
    );
  };

  const renderJoinedGroups = () => {
    if (joinedGroups.length === 0) {
      return (
        <div className="text-center py-12">
          <div className="mb-4">🤝</div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No Groups Joined</h3>
          <p className="text-gray-600">
            You haven't joined any volunteer groups yet. Browse available groups to get started!
          </p>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {joinedGroups.map((group) => (
          <VolunteerGroupCard
            key={group._id}
            group={group}
            currentUser={currentUser}
            onLeave={() => handleLeaveGroup(group)}
            onViewDetails={() => {
              setSelectedGroup(group);
              setShowGroupDetailsModal(true);
            }}
            userLocation={userLocation}
            calculateDistance={calculateDistance}
            isMember={true}
          />
        ))}
      </div>
    );
  };

  const renderGroupMap = () => {
    if (!userLocation || volunteerGroups.length === 0) {
      return (
        <div className="flex items-center justify-center h-96 bg-gray-50 rounded-lg">
          <div className="text-center">
            <div className="mb-4">🗺️</div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">Map Not Available</h3>
            <p className="text-gray-600">
              {!userLocation ? 'Location access required to show map' : 'No volunteer groups to display'}
            </p>
          </div>
        </div>
      );
    }

    return (
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div style={{ height: '500px', width: '100%' }}>
          <MapContainer
            center={[userLocation.lat, userLocation.lng]}
            zoom={12}
            scrollWheelZoom={true}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            {/* User location marker */}
            <Marker position={[userLocation.lat, userLocation.lng]}>
              <Popup>
                <div className="p-2">
                  <h3 className="font-bold">📍 Your Location</h3>
                  <p className="text-sm">Find volunteer groups near you</p>
                </div>
              </Popup>
            </Marker>

            {/* Volunteer group markers */}
            {volunteerGroups.map((group) => {
              if (!group.complaintLocation?.latitude || !group.complaintLocation?.longitude) return null;

              const isOwner = group.createdBy === currentUser._id;
              const memberCount = group.members ? group.members.length : 1;

              return (
                <Marker
                  key={group._id}
                  position={[group.complaintLocation.latitude, group.complaintLocation.longitude]}
                  icon={createGroupIcon(memberCount, isOwner)}
                >
                  <Popup>
                    <div className="p-2 min-w-48">
                      <h3 className="font-bold text-sm mb-2">{group.name}</h3>
                      <p className="text-xs text-gray-600 mb-2">{group.complaintTitle}</p>
                      <div className="text-xs mb-2">
                        <span className="font-medium">Members:</span> {memberCount}/{group.maxMembers}
                      </div>
                      <div className="text-xs mb-3">
                        <span className="font-medium">Leader:</span> {group.createdByUsername}
                      </div>
                      {!isOwner && (!group.members || !group.members.some(m => m.userId === currentUser._id)) && (
                        <button
                          onClick={() => handleJoinGroup(group)}
                          className="w-full px-3 py-1 bg-green-600 text-white text-xs rounded hover:bg-green-700"
                          disabled={group.members && group.members.length >= group.maxMembers}
                        >
                          {group.members && group.members.length >= group.maxMembers ? 'Group Full' : '🤝 Join Group'}
                        </button>
                      )}
                    </div>
                  </Popup>
                </Marker>
              );
            })}
          </MapContainer>
        </div>
      </div>
    );
  };

  // Tab configuration
  // FIXED: Calculate correct available groups count
  // FIXED: Calculate correct counts dynamically
  const calculateAvailableGroups = () => {
    return volunteerGroups.filter(g =>
      g.status === 'open' &&
      g.createdBy !== currentUser?._id &&
      (!g.members || !g.members.some(m => m.userId === currentUser?._id))
    ).length;
  };

  const availableGroupsCount = calculateAvailableGroups();

  console.log('📊 Tab counts:', {
    available: availableGroupsCount,
    myGroups: myGroups.length,
    joined: joinedGroups.length
  });

  // Tab configuration with correct counts
  const tabs = [
    {
      label: `Browse Groups (${availableGroupsCount})`,
      content: renderBrowseGroups()
    },
    {
      label: `My Groups (${myGroups.length})`,
      content: renderMyGroups()
    },
    {
      label: `Joined Groups (${joinedGroups.length})`,
      content: renderJoinedGroups()
    },
    {
      label: 'Group Map',
      content: renderGroupMap()
    }
  ];



  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100">
        <Navbar />
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <div className="text-center py-12">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
              <p className="text-gray-500">Loading volunteer groups...</p>
            </div>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">

          {/* Header */}
          <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">👥 Volunteer Groups</h1>
              <p className="mt-1 text-sm text-gray-500">
                Create or join volunteer groups to work together on complaints • Welcome, {currentUser?.username || 'Volunteer'}!
              </p>
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-3">
              <button
                onClick={() => {
                  fetchComplaints();
                  fetchVolunteerGroups();
                }}
                className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
              >
                🔄 Refresh
              </button>
              {/* // Add this button temporarily in your header section for debugging */}
              <button
                onClick={() => {
                  console.log('🐛 DEBUG INFO:');
                  console.log('Current user:', currentUser);
                  console.log('All groups:', volunteerGroups);
                  console.log('My groups:', myGroups);
                  console.log('Joined groups:', joinedGroups);
                  volunteerGroups.forEach(g => {
                    console.log(`Group "${g.name}":`, {
                      createdBy: g.createdBy,
                      members: g.members,
                      isMyGroup: g.createdBy === currentUser?._id,
                      amIMember: g.members ? g.members.some(m => m.userId === currentUser?._id) : false
                    });
                  });
                }}
                className="px-3 py-1 bg-yellow-500 text-white text-xs rounded"
              >
                🐛 Debug
              </button>
              {/* // Add this button to your Volunteer component header */}
              // Update your force refresh button
              <button
                onClick={async () => {
                  console.log('🔄 Force refresh triggered - clearing all states');

                  // Clear all group-related states
                  setVolunteerGroups([]);
                  setMyGroups([]);
                  setJoinedGroups([]);

                  // Clear any selected states
                  setSelectedGroup(null);

                  // Wait for state clearing
                  await new Promise(resolve => setTimeout(resolve, 200));

                  // Fetch fresh data
                  await fetchVolunteerGroups();

                  console.log('🔄 Force refresh complete');
                }}
                className="px-3 py-1 bg-red-500 text-white text-xs rounded"
              >
                🔄 Force Refresh
              </button>



            </div>
          </div>

          {/* Quick Stats */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div className="bg-white p-4 rounded-lg shadow">
              <div className="text-2xl font-bold text-blue-600">{volunteerGroups.length}</div>
              <div className="text-sm text-gray-500">Total Groups</div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow">
              <div className="text-2xl font-bold text-green-600">{myGroups.length}</div>
              <div className="text-sm text-gray-500">Groups You Created</div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow">
              <div className="text-2xl font-bold text-purple-600">{joinedGroups.length}</div>
              <div className="text-sm text-gray-500">Groups You Joined</div>
            </div>
            <div className="bg-white p-4 rounded-lg shadow">
              <div className="text-2xl font-bold text-yellow-600">
                {volunteerGroups.filter(g => g.status === 'open').length}
              </div>
              <div className="text-sm text-gray-500">Open Groups</div>
            </div>
          </div>

          {/* Main Content */}
          <div className="bg-white shadow overflow-hidden sm:rounded-lg">
            <TabSwitcher tabs={tabs} />
          </div>

          {/* Create Group Modal */}
          {showCreateGroupModal && (
            <CreateGroupModal
              isOpen={showCreateGroupModal}
              onClose={() => {
                setShowCreateGroupModal(false);
                setSelectedComplaint(null);
                setGroupForm({
                  name: '',
                  description: '',
                  maxMembers: 5,
                  skills: '',
                  complaintId: null
                });
              }}
              groupForm={groupForm}
              setGroupForm={setGroupForm}
              selectedComplaint={selectedComplaint}
              setSelectedComplaint={setSelectedComplaint}
              complaints={complaints}
              onSubmit={handleCreateGroup}
            />
          )}

          {/* Group Details Modal */}
          {showGroupDetailsModal && selectedGroup && (
            <GroupDetailsModal
              isOpen={showGroupDetailsModal}
              onClose={() => {
                setShowGroupDetailsModal(false);
                setSelectedGroup(null);
              }}
              group={selectedGroup}
              currentUser={currentUser}
              onJoin={() => handleJoinGroup(selectedGroup)}
              onLeave={() => handleLeaveGroup(selectedGroup)}
              onDelete={() => handleDeleteGroup(selectedGroup)}
            />
          )}
        </div>
      </main>
    </div>
  );
}

// Volunteer Group Card Component
const VolunteerGroupCard = ({
  group,
  currentUser,
  onJoin,
  onLeave,
  onDelete,
  onViewDetails,
  userLocation,
  calculateDistance,
  isOwner = false,
  isMember = false,
  showActions = false
}) => {
  const memberCount = group.members ? group.members.length : 1;
  const isFull = memberCount >= group.maxMembers;

  // Calculate distance if user location is available
  let distance = null;
  if (userLocation && group.complaintLocation?.latitude && group.complaintLocation?.longitude) {
    distance = calculateDistance(
      userLocation.lat,
      userLocation.lng,
      group.complaintLocation.latitude,
      group.complaintLocation.longitude
    );
  }

  return (
    <div className={`p-6 rounded-lg border ${isOwner ? 'bg-blue-50 border-blue-200' :
      isMember ? 'bg-green-50 border-green-200' :
        'bg-white border-gray-200'
      } hover:shadow-lg transition-shadow`}>

      {/* Header */}
      <div className="flex justify-between items-start mb-4">
        <div className="flex-1">
          <div className="flex items-center gap-2 mb-2">
            <h3 className="text-lg font-semibold text-gray-900">{group.name}</h3>
            {isOwner && <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">Leader</span>}
            {isMember && <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">Member</span>}
          </div>
          <p className="text-sm text-gray-600 mb-2">{group.description}</p>
          <div className="flex items-center gap-4 text-sm text-gray-500">
            <span>📍 For: <strong>{group.complaintTitle}</strong></span>
            {distance && <span>🚶 {distance.toFixed(1)} km away</span>}
          </div>
        </div>
        <div className="text-right">
          <div className="text-sm text-gray-500 mb-1">Members</div>
          <div className={`text-lg font-bold ${isFull ? 'text-red-600' : 'text-green-600'}`}>
            {memberCount}/{group.maxMembers}
          </div>
        </div>
      </div>

      {/* Skills Required */}
      {group.requiredSkills && group.requiredSkills.length > 0 && (
        <div className="mb-4">
          <div className="text-xs text-gray-500 mb-1">Skills Needed:</div>
          <div className="flex flex-wrap gap-1">
            {group.requiredSkills.map((skill, index) => (
              <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded">
                {skill}
              </span>
            ))}
          </div>
        </div>
      )}

      {/* Members List */}
      <div className="mb-4">
        <div className="text-xs text-gray-500 mb-2">Group Members:</div>
        <div className="flex flex-wrap gap-2">
          {group.members && group.members.map((member, index) => (
            <div key={index} className={`flex items-center gap-1 px-2 py-1 rounded text-xs ${member.role === 'leader' ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-700'
              }`}>
              {member.role === 'leader' ? '👑' : '👤'} {member.username}
            </div>
          ))}
        </div>
      </div>

      {/* Actions */}
      <div className="flex gap-2">
        <button
          onClick={onViewDetails}
          className="px-3 py-1 bg-gray-100 text-gray-700 rounded text-sm hover:bg-gray-200"
        >
          📋 View Details
        </button>

        {showActions && onJoin && (
          <button
            onClick={onJoin}
            disabled={isFull}
            className={`px-3 py-1 rounded text-sm ${isFull
              ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
              : 'bg-green-600 text-white hover:bg-green-700'
              }`}
          >
            {isFull ? 'Group Full' : '🤝 Join Group'}
          </button>
        )}

        {isMember && onLeave && (
          <button
            onClick={onLeave}
            className="px-3 py-1 bg-yellow-600 text-white rounded text-sm hover:bg-yellow-700"
          >
            🚪 Leave Group
          </button>
        )}

        {isOwner && onDelete && (
          <button
            onClick={onDelete}
            className="px-3 py-1 bg-red-600 text-white rounded text-sm hover:bg-red-700"
          >
            🗑️ Delete Group
          </button>
        )}
      </div>

      {/* Status */}
      <div className="mt-4 pt-4 border-t border-gray-200 text-xs text-gray-500 flex justify-between">
        <span>Created by: <strong>{group.createdByUsername}</strong></span>
        <span>Status: <strong className="capitalize">{group.status}</strong></span>
      </div>
    </div>
  );
};

// Create Group Modal Component
const CreateGroupModal = ({
  isOpen,
  onClose,
  groupForm,
  setGroupForm,
  selectedComplaint,
  setSelectedComplaint,
  complaints,
  onSubmit
}) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-gray-900">Create Volunteer Group</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-4">
            {/* Select Complaint */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select Complaint to Work On *
              </label>
              <select
                value={selectedComplaint?._id || ''}
                onChange={(e) => {
                  const complaint = complaints.find(c => c._id === e.target.value);
                  setSelectedComplaint(complaint);
                }}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                required
              >
                <option value="">Choose a complaint...</option>
                {complaints.map((complaint) => (
                  <option key={complaint._id} value={complaint._id}>
                    {complaint.category} - {complaint.address.substring(0, 50)}...
                  </option>
                ))}
              </select>
            </div>

            {/* Selected Complaint Details */}
            {selectedComplaint && (
              <div className="bg-blue-50 p-3 rounded-lg border border-blue-200">
                <h4 className="font-medium text-blue-900 mb-1">Selected Complaint:</h4>
                <p className="text-sm text-blue-800 mb-1">{selectedComplaint.category}</p>
                <p className="text-xs text-blue-600">{selectedComplaint.description}</p>
                <p className="text-xs text-blue-600">📍 {selectedComplaint.address}</p>
              </div>
            )}

            {/* Group Name */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Group Name *
              </label>
              <input
                type="text"
                value={groupForm.name}
                onChange={(e) => setGroupForm(prev => ({ ...prev, name: e.target.value }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="e.g., Downtown Cleanup Crew"
                required
              />
            </div>

            {/* Group Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Description
              </label>
              <textarea
                value={groupForm.description}
                onChange={(e) => setGroupForm(prev => ({ ...prev, description: e.target.value }))}
                rows={3}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="Describe what your group will do to help resolve this complaint..."
              />
            </div>

            {/* Max Members */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Maximum Members
              </label>
              <select
                value={groupForm.maxMembers}
                onChange={(e) => setGroupForm(prev => ({ ...prev, maxMembers: parseInt(e.target.value) }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
              >
                <option value={2}>2 members</option>
                <option value={3}>3 members</option>
                <option value={4}>4 members</option>
                <option value={5}>5 members</option>
                <option value={6}>6 members</option>
                <option value={8}>8 members</option>
                <option value={10}>10 members</option>
              </select>
            </div>

            {/* Required Skills */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Required Skills (Optional)
              </label>
              <input
                type="text"
                value={groupForm.skills}
                onChange={(e) => setGroupForm(prev => ({ ...prev, skills: e.target.value }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="e.g., Cleaning tools, Photography, Heavy lifting (comma separated)"
              />
              <p className="text-xs text-gray-500 mt-1">
                List skills needed for this task, separated by commas
              </p>
            </div>
          </div>

          {/* Actions */}
          <div className="mt-6 flex justify-end gap-3">
            <button
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              onClick={onSubmit}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              👥 Create Group
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Group Details Modal Component
const GroupDetailsModal = ({
  isOpen,
  onClose,
  group,
  currentUser,
  onJoin,
  onLeave,
  onDelete
}) => {
  if (!isOpen || !group) return null;

  const isOwner = group.createdBy === currentUser._id;
  const isMember = group.members && group.members.some(m => m.userId === currentUser._id);
  const memberCount = group.members ? group.members.length : 1;
  const isFull = memberCount >= group.maxMembers;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-gray-900">Group Details</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-6">
            {/* Group Info */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">{group.name}</h3>
              {group.description && (
                <p className="text-gray-600 mb-4">{group.description}</p>
              )}
            </div>

            {/* Complaint Info */}
            <div className="bg-blue-50 p-4 rounded-lg">
              <h4 className="font-medium text-blue-900 mb-2">📋 Working On:</h4>
              <p className="text-blue-800 font-medium">{group.complaintTitle}</p>
              {group.complaintLocation && (
                <p className="text-sm text-blue-600">📍 {group.complaintLocation.address}</p>
              )}
            </div>

            {/* Group Stats */}
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="text-sm text-gray-500">Members</div>
                <div className="text-xl font-bold text-gray-900">{memberCount}/{group.maxMembers}</div>
              </div>
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="text-sm text-gray-500">Status</div>
                <div className="text-xl font-bold text-gray-900 capitalize">{group.status}</div>
              </div>
            </div>

            {/* Required Skills */}
            {group.requiredSkills && group.requiredSkills.length > 0 && (
              <div>
                <h4 className="font-medium text-gray-900 mb-2">🛠️ Skills Needed:</h4>
                <div className="flex flex-wrap gap-2">
                  {group.requiredSkills.map((skill, index) => (
                    <span key={index} className="px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Members */}
            <div>
              <h4 className="font-medium text-gray-900 mb-3">👥 Group Members:</h4>
              <div className="space-y-2">
                {group.members && group.members.map((member, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center gap-3">
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm ${member.role === 'leader' ? 'bg-blue-100 text-blue-800' : 'bg-green-100 text-green-800'
                        }`}>
                        {member.role === 'leader' ? '👑' : '👤'}
                      </div>
                      <div>
                        <div className="font-medium text-gray-900">{member.username}</div>
                        <div className="text-sm text-gray-500 capitalize">{member.role}</div>
                      </div>
                    </div>
                    <div className="text-xs text-gray-500">
                      Joined {new Date(member.joinedAt).toLocaleDateString()}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Group Leader Info */}
            <div className="border-t pt-4">
              <div className="text-sm text-gray-500">
                Created by <strong>{group.createdByUsername}</strong> on{' '}
                {new Date(group.createdAt).toLocaleDateString()}
              </div>
            </div>
          </div>

          {/* Actions */}
          <div className="mt-6 flex justify-end gap-3">
            <button
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
            >
              Close
            </button>

            {!isOwner && !isMember && onJoin && (
              <button
                onClick={() => {
                  onJoin();
                  onClose();
                }}
                disabled={isFull}
                className={`px-4 py-2 rounded-md ${isFull
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-green-600 text-white hover:bg-green-700'
                  }`}
              >
                {isFull ? 'Group Full' : '🤝 Join Group'}
              </button>
            )}

            {isMember && !isOwner && onLeave && (
              <button
                onClick={() => {
                  onLeave();
                  onClose();
                }}
                className="px-4 py-2 bg-yellow-600 text-white rounded-md hover:bg-yellow-700"
              >
                🚪 Leave Group
              </button>
            )}

            {isOwner && onDelete && (
              <button
                onClick={() => {
                  onDelete();
                  onClose();
                }}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
              >
                🗑️ Delete Group
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Volunteer;
