import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
const navigate = useNavigate();


function Dashboard() {
  const [activeTab, setActiveTab] = useState('complaint');

  const renderTabContent = () => {
    switch (activeTab) {
      case 'complaint':
        return (
          <div>
            <h3>Complaint Registration Form</h3>
            {/* TODO: Add complaint form here */}
            <p>[Complaint form goes here]</p>
          </div>
        );
      case 'volunteer':
        return (
          <div>
            <h3>Join as Volunteer</h3>
            {/* TODO: Add volunteer form here */}
            <p>[Volunteer form goes here]</p>
          </div>
        );
      case 'active':
        return (
          <div>
            <h3>Active Complaints</h3>
            {/* TODO: Fetch and display active complaints */}
            <p>[Active complaints list goes here]</p>
          </div>
        );
      case 'solved':
        return (
          <div>
            <h3>Solved Complaints</h3>
            {/* TODO: Fetch and display solved complaints */}
            <p>[Solved complaints list goes here]</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Welcome to Complaint Tracker</h2>
      <div style={styles.tabBar}>
        <button onClick={() => navigate('/complaint')}>Register a Complaint</button>
        <button onClick={() => setActiveTab('volunteer')}>Become Volunteer</button>
        <button onClick={() => setActiveTab('active')}>Active Complaints</button>
        <button onClick={() => setActiveTab('solved')}>Solved Complaints</button>
      </div>
      <div style={{ marginTop: '2rem' }}>
        {renderTabContent()}
      </div>
    </div>
  );
}

const styles = {
  tabBar: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem',
    flexWrap: 'wrap'
  }
};

export default Dashboard;
