import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [activeTab, setActiveTab] = useState('complaint');
  const navigate = useNavigate(); // ✅ required

  return (
    <div style={{ padding: '2rem' }}>
      <h2>Welcome to Complaint Tracker</h2>
      <div style={styles.tabBar}>
        <button onClick={() => navigate('/ReComplaint')}>Register Complaint</button>
        <button onClick={() => setActiveTab('volunteer')}>Become Volunteer</button>
        <button onClick={() => setActiveTab('active')}>Active Complaints</button>
        <button onClick={() => setActiveTab('solved')}>Solved Complaints</button>
      </div>

      {/* Other content based on tab */}
    </div>
  );
}

const styles = {
  tabBar: {
    display: 'flex',
    gap: '1rem',
    marginBottom: '1rem',
    flexWrap: 'wrap'
  }
};

export default Dashboard;
