import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

function RegisterComplaint() {
  const location = useLocation();
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const photoRef = useRef(null);
  const fileInputRef = useRef(null);
  
  const [formData, setFormData] = useState({
    category: 'Garbage',
    description: '',
    address: '',
    lat: '',
    lng: '',
    priority: 'Medium'
  });
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [showCamera, setShowCamera] = useState(false);
  const [cameraStream, setCameraStream] = useState(null);
  
  // Location state management
  const [locationMode, setLocationMode] = useState('device'); // 'device' or 'manual'
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [locationError, setLocationError] = useState('');
  
  // Check if we're in capture mode from the URL query parameter
  const captureMode = new URLSearchParams(location.search).get('capture') === 'true';

  useEffect(() => {
    // Auto-get device location when component mounts and mode is device
    if (locationMode === 'device') {
      getCurrentLocation();
    }
    
    // If in capture mode, automatically open the camera
    if (captureMode) {
      startCamera();
    }
    
    // Cleanup function to stop camera when component unmounts
    return () => {
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [captureMode]);

  // Enhanced geolocation function
  const getCurrentLocation = () => {
    setIsGettingLocation(true);
    setLocationError('');
    
    if (!navigator.geolocation) {
      setLocationError('Geolocation is not supported by this browser');
      setIsGettingLocation(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        setFormData(prev => ({
          ...prev,
          lat: position.coords.latitude.toFixed(6),
          lng: position.coords.longitude.toFixed(6),
        }));
        setIsGettingLocation(false);
        
        // Try to get address from coordinates
        reverseGeocode(position.coords.latitude, position.coords.longitude);
      },
      (error) => {
        console.error('Geolocation error:', error);
        let errorMessage = 'Unable to get your location. ';
        switch(error.code) {
          case error.PERMISSION_DENIED:
            errorMessage += 'Location access denied by user.';
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage += 'Location information unavailable.';
            break;
          case error.TIMEOUT:
            errorMessage += 'Location request timed out.';
            break;
          default:
            errorMessage += 'An unknown error occurred.';
        }
        setLocationError(errorMessage);
        setIsGettingLocation(false);
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000
      }
    );
  };

  // Reverse geocoding to get address from coordinates
  const reverseGeocode = async (lat, lng) => {
    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=18&addressdetails=1`
      );
      const data = await response.json();
      
      if (data && data.display_name) {
        setFormData(prev => ({
          ...prev,
          address: data.display_name
        }));
      }
    } catch (error) {
      console.error('Reverse geocoding error:', error);
      // Don't show error to user, address field remains editable
    }
  };

  // Handle location mode change
  const handleLocationModeChange = (mode) => {
    setLocationMode(mode);
    setLocationError('');
    
    if (mode === 'device') {
      getCurrentLocation();
    } else {
      // Clear coordinates when switching to manual
      setFormData(prev => ({
        ...prev,
        lat: '',
        lng: ''
      }));
    }
  };
  
  const startCamera = async () => {
    try {
      // Stop any existing stream first
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
      }
      
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'environment',
          width: { ideal: 1280 },
          height: { ideal: 720 }
        }, 
        audio: false 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play().catch(err => {
            console.error('Error playing video:', err);
          });
        };
        setCameraStream(stream);
        setShowCamera(true);
      }
    } catch (err) {
      console.error('Error accessing camera:', err);
      alert('Could not access the camera. Please check permissions or use file upload instead.');
    }
  };
  
  const takePhoto = () => {
    if (videoRef.current && photoRef.current) {
      const video = videoRef.current;
      const photo = photoRef.current;
      const ctx = photo.getContext('2d');
      
      // Set canvas dimensions to match video
      photo.width = video.videoWidth;
      photo.height = video.videoHeight;
      
      // Draw the video frame to the canvas
      ctx.drawImage(video, 0, 0, photo.width, photo.height);
      
      // Convert canvas to data URL with better quality
      const dataUrl = photo.toDataURL('image/jpeg', 0.8);
      setImagePreview(dataUrl);
      
      // Create a file-like object from the data URL
      fetch(dataUrl)
        .then(res => res.blob())
        .then(blob => {
          const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
          const file = new File([blob], `complaint-photo-${timestamp}.jpg`, { type: 'image/jpeg' });
          setImage(file);
        });
      
      // Stop the camera stream
      if (cameraStream) {
        cameraStream.getTracks().forEach(track => track.stop());
        setCameraStream(null);
      }
      
      setShowCamera(false);
    }
  };
  
  const closeCamera = () => {
    if (cameraStream) {
      cameraStream.getTracks().forEach(track => track.stop());
      setCameraStream(null);
    }
    setShowCamera(false);
    
    // If we were in capture mode, navigate back to dashboard
    if (captureMode) {
      navigate('/dashboard');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        alert('Image size should be less than 5MB');
        return;
      }
      
      // Validate file type
      if (!file.type.startsWith('image/')) {
        alert('Please select a valid image file');
        return;
      }
      
      setImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    // Basic validation
    if (!formData.address.trim()) {
      alert('Please provide an address or location');
      setIsSubmitting(false);
      return;
    }
    
    try {
      // Import the complaint service
      const { createComplaint } = await import('../services/complaintService');
      
      // Create a new complaint object
      const newComplaint = {
        category: formData.category,
        description: formData.description,
        priority: formData.priority,
        address: formData.address,
        latitude: formData.lat ? parseFloat(formData.lat) : null,
        longitude: formData.lng ? parseFloat(formData.lng) : null,
        image: imagePreview,
        status: 'pending'
      };
      
      // Send the complaint to the backend API
      const response = await createComplaint(newComplaint);
      console.log('Complaint submitted:', response.data);
      
      setIsSubmitting(false);
      setSubmitSuccess(true);
      
      // Reset form after 3 seconds
      setTimeout(() => {
        setFormData({
          category: 'Garbage',
          description: '',
          address: '',
          lat: locationMode === 'device' ? formData.lat : '',
          lng: locationMode === 'device' ? formData.lng : '',
          priority: 'Medium'
        });
        setImage(null);
        setImagePreview(null);
        setSubmitSuccess(false);
        if (fileInputRef.current) {
          fileInputRef.current.value = '';
        }
      }, 3000);
    } catch (error) {
      console.error('Error submitting complaint:', error);
      setIsSubmitting(false);
      alert('Failed to submit complaint. Please try again.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <Navbar />
      
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-white shadow-lg rounded-lg overflow-hidden">
            <div className="px-6 py-8 bg-gradient-to-r from-blue-600 to-blue-800 sm:p-10 sm:pb-6">
              <h2 className="text-2xl font-bold text-white">Register a Complaint</h2>
              <p className="mt-2 text-blue-100">Fill out the form below to submit your complaint</p>
            </div>
            
            {/* Enhanced Camera UI */}
            {showCamera && (
              <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
                <div className="bg-white p-4 rounded-lg max-w-lg w-full mx-4">
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-medium">Take a Photo</h3>
                    <button 
                      onClick={closeCamera}
                      className="text-gray-500 hover:text-gray-700"
                    >
                      <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                      </svg>
                    </button>
                  </div>
                  
                  <div className="relative bg-black rounded-lg overflow-hidden">
                    <video 
                      ref={videoRef} 
                      autoPlay 
                      playsInline 
                      muted
                      className="w-full h-auto"
                      style={{ maxHeight: '400px' }}
                    />
                    <canvas ref={photoRef} className="hidden" />
                  </div>
                  
                  <div className="mt-4 flex justify-center space-x-3">
                    <button
                      onClick={closeCamera}
                      className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                    >
                      Cancel
                    </button>
                    <button
                      onClick={takePhoto}
                      className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      <svg className="-ml-1 mr-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                      Capture Photo
                    </button>
                  </div>
                </div>
              </div>
            )}
            
            <div className="px-6 py-8 sm:p-10">
              {submitSuccess ? (
                <div className="bg-green-50 border-l-4 border-green-500 p-4 mb-6">
                  <div className="flex">
                    <div className="flex-shrink-0">
                      <svg className="h-5 w-5 text-green-400" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <div className="ml-3">
                      <p className="text-sm text-green-700">Your complaint has been successfully submitted!</p>
                    </div>
                  </div>
                </div>
              ) : null}
              
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 gap-y-6 gap-x-4 sm:grid-cols-6">
                  <div className="sm:col-span-3">
                    <label htmlFor="category" className="block text-sm font-medium text-gray-700">Category</label>
                    <div className="mt-1">
                      <select
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                      >
                        <option value="Garbage">Garbage</option>
                        <option value="Water Leakage">Water Leakage</option>
                        <option value="Pothole">Pothole</option>
                        <option value="Electricity Issue">Electricity Issue</option>
                        <option value="Street Light">Street Light</option>
                        <option value="Sewage">Sewage</option>
                        <option value="Other">Other</option>
                      </select>
                    </div>
                  </div>

                  <div className="sm:col-span-3">
                    <label htmlFor="priority" className="block text-sm font-medium text-gray-700">Priority</label>
                    <div className="mt-1">
                      <select
                        id="priority"
                        name="priority"
                        value={formData.priority}
                        onChange={handleChange}
                        className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                      >
                        <option value="Low">Low</option>
                        <option value="Medium">Medium</option>
                        <option value="High">High</option>
                      </select>
                    </div>
                  </div>

                  <div className="sm:col-span-6">
                    <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
                    <div className="mt-1">
                      <textarea
                        id="description"
                        name="description"
                        rows={4}
                        value={formData.description}
                        onChange={handleChange}
                        placeholder="Please provide detailed information about the issue"
                        className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                        required
                      />
                    </div>
                  </div>

                  {/* Enhanced Location Section */}
                  <div className="sm:col-span-6">
                    <label className="block text-sm font-medium text-gray-700 mb-3">Location</label>
                    
                    {/* Location Mode Toggle */}
                    <div className="flex space-x-4 mb-4">
                      <label className="flex items-center">
                        <input
                          type="radio"
                          name="locationMode"
                          value="device"
                          checked={locationMode === 'device'}
                          onChange={() => handleLocationModeChange('device')}
                          className="form-radio h-4 w-4 text-blue-600"
                        />
                        <span className="ml-2 text-sm text-gray-700">Use Device Location</span>
                      </label>
                      <label className="flex items-center">
                        <input
                          type="radio"
                          name="locationMode"
                          value="manual"
                          checked={locationMode === 'manual'}
                          onChange={() => handleLocationModeChange('manual')}
                          className="form-radio h-4 w-4 text-blue-600"
                        />
                        <span className="ml-2 text-sm text-gray-700">Enter Manually</span>
                      </label>
                    </div>

                    {/* Location Error */}
                    {locationError && (
                      <div className="mb-3 p-2 bg-red-50 border border-red-200 rounded-md">
                        <p className="text-sm text-red-600">{locationError}</p>
                      </div>
                    )}

                    {/* Device Location Section */}
                    {locationMode === 'device' && (
                      <div className="mb-4">
                        <button
                          type="button"
                          onClick={getCurrentLocation}
                          disabled={isGettingLocation}
                          className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-blue-700 bg-blue-100 hover:bg-blue-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
                        >
                          {isGettingLocation ? (
                            <>
                              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-blue-700" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                              </svg>
                              Getting Location...
                            </>
                          ) : (
                            <>
                              <svg className="-ml-1 mr-2 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                              </svg>
                              Get Current Location
                            </>
                          )}
                        </button>
                      </div>
                    )}
                  </div>

                  <div className="sm:col-span-6">
                    <label htmlFor="address" className="block text-sm font-medium text-gray-700">
                      Address {locationMode === 'manual' ? '*' : '(Auto-filled from location)'}
                    </label>
                    <div className="mt-1">
                      <input
                        type="text"
                        id="address"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        placeholder={locationMode === 'device' ? 'Address will be auto-filled' : 'Enter the location address'}
                        className="shadow-sm focus:ring-blue-500 focus:border-blue-500 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                        required
                      />
                    </div>
                  </div>

                  {/* Show coordinates only when using device location */}
                  {locationMode === 'device' && (formData.lat || formData.lng) && (
                    <>
                      <div className="sm:col-span-3">
                        <label htmlFor="lat" className="block text-sm font-medium text-gray-700">Latitude</label>
                        <div className="mt-1">
                          <input
                            type="text"
                            id="lat"
                            name="lat"
                            value={formData.lat}
                            readOnly
                            className="shadow-sm bg-gray-50 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                          />
                        </div>
                      </div>

                      <div className="sm:col-span-3">
                        <label htmlFor="lng" className="block text-sm font-medium text-gray-700">Longitude</label>
                        <div className="mt-1">
                          <input
                            type="text"
                            id="lng"
                            name="lng"
                            value={formData.lng}
                            readOnly
                            className="shadow-sm bg-gray-50 block w-full sm:text-sm border-gray-300 rounded-md p-2"
                          />
                        </div>
                      </div>
                    </>
                  )}

                  <div className="sm:col-span-6">
                    <label htmlFor="image" className="block text-sm font-medium text-gray-700">Upload Image</label>
                    <div className="mt-1 flex items-center flex-wrap gap-3">
                      <input
                        ref={fileInputRef}
                        type="file"
                        id="image"
                        accept="image/*"
                        onChange={handleImageChange}
                        className="sr-only"
                      />
                      <label
                        htmlFor="image"
                        className="relative cursor-pointer bg-white py-2 px-3 border border-gray-300 rounded-md shadow-sm text-sm leading-4 font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        <span>Choose file</span>
                      </label>
                      <button
                        type="button"
                        onClick={startCamera}
                        className="relative cursor-pointer bg-blue-600 py-2 px-3 border border-transparent rounded-md shadow-sm text-sm leading-4 font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        <span>Take Photo</span>
                      </button>
                      <span className="text-sm text-gray-500">
                        {image ? (image.name || 'Photo captured') : 'No file chosen'}
                      </span>
                    </div>
                    <p className="mt-1 text-xs text-gray-500">
                      Supported formats: JPG, PNG, GIF. Max size: 5MB
                    </p>
                  </div>

                  {imagePreview && (
                    <div className="sm:col-span-6">
                      <label className="block text-sm font-medium text-gray-700">Image Preview</label>
                      <div className="mt-1 relative inline-block">
                        <img src={imagePreview} alt="Preview" className="h-40 w-auto object-cover rounded-md" />
                        <button
                          type="button"
                          onClick={() => {
                            setImage(null);
                            setImagePreview(null);
                            if (fileInputRef.current) {
                              fileInputRef.current.value = '';
                            }
                          }}
                          className="absolute top-2 right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                        >
                          <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      </div>
                    </div>
                  )}
                </div>

                <div className="pt-5">
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      className="bg-white py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      onClick={() => navigate('/dashboard')}
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={isSubmitting}
                      className={`inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${isSubmitting ? 'opacity-75 cursor-not-allowed' : ''}`}
                    >
                      {isSubmitting ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Submitting...
                        </>
                      ) : 'Submit Complaint'}
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default RegisterComplaint;
