import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import TabSwitcher from '../components/TabSwitcher';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import api from '../api';

// Fix for default marker icon issue in react-leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// Custom group marker icon
const createGroupIcon = (memberCount, isOwner) => {
  const color = isOwner ? '#dc2626' : '#059669'; // Red for owner, green for member

  return L.divIcon({
    className: 'group-marker',
    html: `
      <div style="
        background-color: ${color};
        width: 28px;
        height: 28px;
        border-radius: 50%;
        border: 3px solid white;
        box-shadow: 0 2px 6px rgba(0,0,0,0.3);
        display: flex;
        align-items: center;
        justify-content: center;
        color: white;
        font-size: 10px;
        font-weight: bold;
      ">${memberCount}</div>
    `,
    iconSize: [28, 28],
    iconAnchor: [14, 14]
  });
};

function Volunteer() {
  const [activeTab, setActiveTab] = useState('browse-groups');
  const [complaints, setComplaints] = useState([]);
  const [volunteerGroups, setVolunteerGroups] = useState([]);
  const [myGroups, setMyGroups] = useState([]);
  const [joinedGroups, setJoinedGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [locationStatus, setLocationStatus] = useState('Getting location...');

  // Modal states
