  isOpen, 
  onClose, 
  groupForm, 
  setGroupForm, 
  selectedComplaint,
  setSelectedComplaint,
  complaints, 
  onSubmit 
}) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-gray-900">Create Volunteer Group</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-4">
            {/* Select Complaint */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select Complaint to Work On *
              </label>
              <select
                value={selectedComplaint?._id || ''}
                onChange={(e) => {
                  const complaint = complaints.find(c => c._id === e.target.value);
                  setSelectedComplaint(complaint);
                }}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                required
              >
                <option value="">Choose a complaint...</option>
                {complaints.map((complaint) => (
                  <option key={complaint._id} value={complaint._id}>
                    {complaint.category} - {complaint.address.substring(0, 50)}...
                  </option>
                ))}
              </select>
            </div>

            {/* Selected Complaint Details */}
            {selectedComplaint && (
              <div className="bg-blue-50 p-3 rounded-lg border border-blue-200">
                <h4 className="font-medium text-blue-900 mb-1">Selected Complaint:</h4>
                <p className="text-sm text-blue-800 mb-1">{selectedComplaint.category}</p>
                <p className="text-xs text-blue-600">{selectedComplaint.description}</p>
                <p className="text-xs text-blue-600">ðŸ“ {selectedComplaint.address}</p>
              </div>
            )}

            {/* Group Name */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Group Name *
              </label>
              <input
                type="text"
                value={groupForm.name}
                onChange={(e) => setGroupForm(prev => ({ ...prev, name: e.target.value }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="e.g., Downtown Cleanup Crew"
                required
              />
            </div>

            {/* Group Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Description
              </label>
              <textarea
                value={groupForm.description}
                onChange={(e) => setGroupForm(prev => ({ ...prev, description: e.target.value }))}
                rows={3}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="Describe what your group will do to help resolve this complaint..."
              />
            </div>

            {/* Max Members */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Maximum Members
              </label>
              <select
                value={groupForm.maxMembers}
                onChange={(e) => setGroupForm(prev => ({ ...prev, maxMembers: parseInt(e.target.value) }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
              >
                <option value={2}>2 members</option>
                <option value={3}>3 members</option>
                <option value={4}>4 members</option>
                <option value={5}>5 members</option>
                <option value={6}>6 members</option>
                <option value={8}>8 members</option>
                <option value={10}>10 members</option>
              </select>
            </div>

            {/* Required Skills */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Required Skills (Optional)
              </label>
              <input
                type="text"
                value={groupForm.skills}
                onChange={(e) => setGroupForm(prev => ({ ...prev, skills: e.target.value }))}
                className="w-full border border-gray-300 rounded-md p-2 text-sm"
                placeholder="e.g., Cleaning tools, Photography, Heavy lifting (comma separated)"
              />
              <p className="text-xs text-gray-500 mt-1">
                List skills needed for this task, separated by commas
              </p>
            </div>
          </div>

          {/* Actions */}
          <div className="mt-6 flex justify-end gap-3">
            <button
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              onClick={onSubmit}
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              ðŸ‘¥ Create Group
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Group Details Modal Component
const GroupDetailsModal = ({ 
  isOpen, 
  onClose, 
  group, 
  currentUser,
  onJoin,
  onLeave,
  onDelete
}) => {
  if (!isOpen || !group) return null;

  const isOwner = group.createdBy === currentUser._id;
  const isMember = group.members && group.members.some(m => m.userId === currentUser._id);
  const memberCount = group.members ? group.members.length : 1;
  const isFull = memberCount >= group.maxMembers;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-gray-900">Group Details</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-6">
            {/* Group Info */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">{group.name}</h3>
              {group.description && (
                <p className="text-gray-600 mb-4">{group.description}</p>
              )}
            </div>

            {/* Complaint Info */}
            <div className="bg-blue-50 p-4 rounded-lg">
              <h4 className="font-medium text-blue-900 mb-2">ðŸ“‹ Working On:</h4>
              <p className="text-blue-800 font-medium">{group.complaintTitle}</p>
              {group.complaintLocation && (
                <p className="text-sm text-blue-600">ðŸ“ {group.complaintLocation.address}</p>
              )}
            </div>

            {/* Group Stats */}
            <div className="grid grid-cols-2 gap-4">
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="text-sm text-gray-500">Members</div>
                <div className="text-xl font-bold text-gray-900">{memberCount}/{group.maxMembers}</div>
              </div>
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="text-sm text-gray-500">Status</div>
                <div className="text-xl font-bold text-gray-900 capitalize">{group.status}</div>
              </div>
            </div>

            {/* Required Skills */}
            {group.requiredSkills && group.requiredSkills.length > 0 && (
              <div>
                <h4 className="font-medium text-gray-900 mb-2">ðŸ› ï¸ Skills Needed:</h4>
                <div className="flex flex-wrap gap-2">
                  {group.requiredSkills.map((skill, index) => (
                    <span key={index} className="px-3 py-1 bg-blue-100 text-blue-800 text-sm rounded-full">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Members */}
            <div>
              <h4 className="font-medium text-gray-900 mb-3">ðŸ‘¥ Group Members:</h4>
              <div className="space-y-2">
                {group.members && group.members.map((member, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center gap-3">
                      <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm ${
                        member.role === 'leader' ? 'bg-blue-100 text-blue-800' : 'bg-green-100 text-green-800'
                      }`}>
                        {member.role === 'leader' ? 'ðŸ‘‘' : 'ðŸ‘¤'}
                      </div>
                      <div>
                        <div className="font-medium text-gray-900">{member.username}</div>
                        <div className="text-sm text-gray-500 capitalize">{member.role}</div>
                      </div>
                    </div>
                    <div className="text-xs text-gray-500">
                      Joined {new Date(member.joinedAt).toLocaleDateString()}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Group Leader Info */}
            <div className="border-t pt-4">
              <div className="text-sm text-gray-500">
                Created by <strong>{group.createdByUsername}</strong> on{' '}
                {new Date(group.createdAt).toLocaleDateString()}
              </div>
            </div>
          </div>

          {/* Actions */}
          <div className="mt-6 flex justify-end gap-3">
            <button
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
            >
              Close
            </button>

            {!isOwner && !isMember && onJoin && (
              <button
                onClick={() => {
                  onJoin();
                  onClose();
                }}
                disabled={isFull}
                className={`px-4 py-2 rounded-md ${
                  isFull 
                    ? 'bg-gray-300 text-gray-500 cursor-not-allowed' 
                    : 'bg-green-600 text-white hover:bg-green-700'
                }`}
              >
                {isFull ? 'Group Full' : 'ðŸ¤ Join Group'}
              </button>
            )}

            {isMember && !isOwner && onLeave && (
              <button
                onClick={() => {
                  onLeave();
                  onClose();
                }}
                className="px-4 py-2 bg-yellow-600 text-white rounded-md hover:bg-yellow-700"
              >
                ðŸšª Leave Group
              </button>
            )}

            {isOwner && onDelete && (
              <button
                onClick={() => {
                  onDelete();
                  onClose();
                }}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
              >
                ðŸ—‘ï¸ Delete Group
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Volunteer;
