import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import ActiveComplaints from './pages/ActiveComplaints';
import SolvedComplaints from './pages/SolvedComplaints';
import RegisterComplaint from './pages/RegisterComplaint'; 

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/active" element={<ActiveComplaints />} />
        <Route path="/solved" element={<SolvedComplaints />} />
        <Route path="/complaint" element={<RegisterComplaint />} />

      </Routes>
    </Router>
  );
}

const PrivateRoute = ({ children }) => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user ? children : <Navigate to="/login" />;
};

// In Routes:
<Route path="/dashboard" element={<PrivateRoute><Dashboard /></PrivateRoute>} />


export default App;
