// src/pages/ForgotPassword.js
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const res = await axios.post("http://localhost:5000/api/auth/request-reset", { email });

      if (res.data.success) {
        localStorage.setItem("resetEmail", email); // store temporarily
        navigate("/reset-password");
      } else {
        setMessage(res.data.message);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 px-4">
      <div className="bg-white shadow-md rounded-md p-6 w-full max-w-md space-y-4">
        <h2 className="text-xl font-semibold text-center">Forgot Password</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Enter your registered email"
            className="w-full p-2 border border-gray-300 rounded-md"
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button type="submit" className="w-full bg-black text-white py-2 rounded-md">
            Send OTP
          </button>
        </form>
        {message && <p className="text-center text-sm text-red-500">{message}</p>}
      </div>
    </div>
  );
}
